INSERT INTO `tintuc` (`id_tin`, `ten`, `urlHinh`, `tomTat`, `noiDung`, `soNguoiXem`, `ngay`, `trangThai`, `id_taiKhoan`, `id_comment`) VALUES
(1, 'Từ tháng 11, người dân TPHCM có thể đi du lịch miền Trung và miền Tây', 'tin1.jpg', 'Sau thời gian thí điểm các tour du lịch nội vùng tới Củ Chi, Cần Giờ và sau đó là tour khép kín với một số địa phương trong khu vực Đông Nam Bộ như Tây Ninh, Vũng Tàu thì giờ đây, ngay trong tháng 11 này, du khách đã có thể đi tour du lịch miền Trung và tham gia tour du lịch miền Tây sông nước.\r\n\r\n', 'TP.HCM vốn là nơi tập trung đông nhất các doanh nghiệp lữ hành trong cả nước, giữ vai trò “đầu mối” đưa khách về các địa phương. Với tình hình dịch COVID-19 đã được kiểm soát và nhiều tỉnh thành trên cả nước thực hiện chính sách nới lỏng giãn cách đã thúc đẩy các công ty du lịch của TP mở bán thêm các tour liên vùng. Đây không chỉ là tín hiệu vui cho riêng TP mà còn góp phần phục hồi du lịch cho các địa phương và vực dậy “ngành công nghiệp không khói” của cả nước, tạo thêm nhiều cơ hội cho du khách giải tỏa căng thẳng sau nhiều ngày “chôn chân”. <br />\r\nhttps://media.vietravel.com/Images/NewsPicture/phuc-hoi-du-lich-mien-trung-mien-tay.jpg<br />\r\nTừ tháng 11/2021 trở đi, du khách Việt Nam có “thẻ xanh COVID” sẽ có thêm nhiều sự lựa chọn khi đi tour trong nước. Hiện đa số các tỉnh, thành đều đang thống nhất phương án mở cửa đồng loạt, nhưng vẫn đảm bảo tiêu chí an toàn. Theo đó, thời gian này người dân TP.HCM đã có thể đi du lịch Long An, Bến Tre, Đồng Tháp với tour 2 ngày 1 đêm hay đi trong ngày. Điểm đến là các Khu du lịch Cánh đồng bất tận, Khu Du Lịch Lan Vương, Khu Du Lịch Xẻo Quýt… Tại đây du khách được tha hồ trải nghiệm liệu pháp "tắm rừng" cũng như chèo xuồng ba lá len lỏi qua các con rạch nhỏ với vô vàn cảm xúc. Song song đó là loạt các sản phẩm Miền Tây truyền thống như tour du lịch 1 ngày khám phá Mỹ Tho - Thới Sơn - Bến Tre; 2 ngày vi vu Mỹ Tho, Thới Sơn, Bến Tre, Cần Thơ, Vĩnh Long trải nghiệm làm gốm, nghỉ dưỡng Homestay Út Trinh; 3 ngày bay đến Phú Quốc ngắm hoàng hôn ở Sunset Sanato hay 4 ngày khám phá xứ dừa Bến Tre, vùng bảy núi An Giang, Cà Mau điểm tận cùng của Tổ Quốc…<br />\r\nhttps://media.vietravel.com/Images/NewsPicture/mien-trung-mo-cua.jpg\r\n<br />\r\nTrở lại sau giai đoạn dịch lần thứ 4, ngoài mở thêm các tour nội địa đến Tây Ninh, Vũng Tàu, các tỉnh miền Tây Nam Bộ, Vietravel còn mở rộng liên kết du lịch miền Trung. Ba tỉnh Khánh Hòa, Phú Yên, Bình Định được đánh giá là những địa phương cơ bản đã kiểm soát được dịch bệnh, đồng thời chưa bị ảnh hưởng nhiều bởi tình hình mưa bão nên phù hợp để du khách đi du lịch tháng 11 này. Cùng với đó, Vietravel đã mở thêm nhiều tour du lịch bằng đường bộ lẫn đường hàng không đến các địa điểm du lịch nổi tiếng ở miền Trung như: Đà Nẵng – Hội An, Quy Nhơn, Nha Trang, Phan Thiết. \r\n<br />\r\nhttps://media.vietravel.com/Images/NewsPicture/mien-trung-mien-tay.jpg\r\n<br />\r\nVới các tour này, người tham gia tour phải có thẻ xanh COVID (đã tiêm vaccine ngừa COVID-19 đủ liều, thời gian hoàn tất mũi 2 trước 14 ngày và không quá 12 tháng), khách là F0 đã khỏi bệnh (có giấy chứng nhận của Sở Y Tế và không quá 12 tháng) và có giấy xác nhận âm tính với COVID-19 còn hiệu lực. ', 10004, '2021-11-06', 1, 2, 0),
(2, 'Săn bình minh cực đẹp ở Tây Ninh với hành trình xanh an toàn', 'tin2.jpg', 'Chỉ cách TP. HCM chưa đến 100 km, Tây Ninh là một điểm tham quan, vui chơi xả stress cực phù hợp vào những dịp nghỉ ngơi ngắn ngày. Vùng đất Thánh yên bình giữa khung cảnh hữu tình, non hòa trong mây là một trong những điểm du lịch rất được ưa chuộng với các tín đồ du lịch hay nhiếp ảnh.', '<b>Trải nghiệm cáp treo xuyên mây</b>\r\nĐược biết đến là “nóc nhà của Nam Bộ”, núi Bà Đen (Tây Ninh) với vẻ đẹp huyền ảo trong nắng sớm chính là điểm săn mây, ngắm bình minh tuyệt đẹp. Vào những ngày thu đẹp trời, ánh nắng vàng lấp lánh trong những tầng mây bồng bềnh là thời điểm để lên cao, nhìn ngắm thiên nhiên cực lý tưởng. Khung cảnh nên thơ cùng chiều cao lý tưởng 986m đã hấp dẫn nhiều người chọn đến núi Bà Đen như một cách để lên cao tận hưởng thiên nhiên, trải nghiệm những khoảnh khắc tuyệt đẹp của đất trời và “săn” ảnh thật chất. <br />\r\nhttps://media.vietravel.com/Images/NewsPicture/nui-ba-den-du-lich.jpg<br />\r\nVới tuyến cáp treo lên đỉnh núi, bạn sẽ được trải nghiệm đi xuyên mây, hòa mình vào cảnh vật thiên nhiên hùng vĩ để chinh phục ngọn núi cao nhất vùng Đông Nam Bộ. Hành trình lên đỉnh núi thiêng sẽ mang đến cho bạn những trải nghiệm tuyệt vời với tầm nhìn 360 độ ngắm trọn Tây Ninh, chạm tay vào mây trời. Đặc biệt, bạn còn có cơ hội check-in nhà ga cáp treo lớn nhất thế giới, với tuyến cáp treo hiện đại hành trình chiêm bái và chinh phục ngọn núi cao của du khách thập phương trở nên dễ dàng hơn bao giờ hết.<br />\r\nhttps://media.vietravel.com/Images/NewsPicture/San-may-tay-ninh.jpg<br />\r\nNói đến leo núi để thưởng ngoạn, việc chờ đón bình minh, hay ngắm nhìn những áng mây trắng xóa, dày đặc như bông trôi lơ lửng giữa khoảng trời mênh mông là điều hầu như ai cũng muốn được một lần trải nghiệm. Nhiều bạn trẻ sau khi đến đỉnh núi Bà Đen đã chọn ở lại qua đêm, để được đón ngày mới, ngắm mặt trời lên. Vẻ huyền ảo khi ngày mới xuất hiện trên đỉnh núi sẽ là một trong những điều khiến bạn nhớ mãi không quên. <br />\r\n\r\n<b>Mê đắm cảnh sắc thiên nhiên hoang sơ và quần thể kiến trúc tâm linh lâu đời</b>\r\n“Biển mây” trên đỉnh núi đẹp xuất sắc, không thua kém các điểm săn mây nổi tiếng khác ở Việt Nam. Đi cáp treo lên đỉnh núi, sẽ có những tháp để bạn check-in và ngắm cảnh tuyệt đẹp. Tuyệt vời nhất là bạn có thể ngắm tượng phật bà Tây Bổ Đà Sơn bay trên biển mây trắng bồng bềnh đẹp như lạc vào xứ sở thần tiên. Chiêm bái Tượng Phật Bà uy nghiêm cao nhất Châu Á và viếng thăm quần thể tâm linh linh thiêng với hàng chục nơi thờ tự, tạo nên sự tôn nghiêm giữa núi rừng cũng là cách để chúng ta tìm sự an yên cho bản thân sau những ngày dịch bệnh diễn ra căng thẳng.<br />\r\nhttps://media.vietravel.com/Images/NewsPicture/tay-bo-da-son_1.jpg<br />\r\nTrải nghiệm tuyến cáp treo Chùa Hang đưa bạn đến Quần Thể Tâm Linh Chùa Bà. Ở độ cao 350m giữa lưng chừng núi là quần thể kiến trúc hang động, chùa chiền mang nét đẹp thiên phú kết hợp cùng bàn tay khéo léo của con người với nhiều truyền thuyết ly kỳ, bí ẩn gồm chùa Bà, chùa Hang, động Hoàng Chung, chùa Trung, chùa Mới… Ấn tượng với những ngôi chùa cổ và di tích lịch sử linh thiêng tại Núi Bà nơi đây đã được công nhận là Di tích lịch sử văn hóa và danh lam thắng cảnh cấp quốc gia.<br />\r\nhttps://media.vietravel.com/Images/NewsPicture/san-may.jpg<br />\r\nĐứng trên cao để được trông xa và tận hưởng sự mát lành từ thiên nhiên, quang cảnh nơi đây như đánh thức sự mơ mộng trong mỗi chúng ta. Bình minh tại núi Bà Đen là sự hòa trộn màu sắc đầy chất thơ chính là thời điểm để cho ra những tấm ảnh chụp cực kỳ lãng mạn. Được chiêm ngưỡng những sắc màu đầy tươi mới ấy sẽ tạo cho ta cảm giác vô cùng thanh tĩnh, nhẹ nhàng. Bạn hãy tranh thủ lên núi vào sáng sớm để được tận hưởng thời khắc có mây nhiều và đẹp nhất trong ngày nhé!', 3339, '2021-11-06', 1, 2, 0),
(3, 'Du lịch tăng nhiệt, TPHCM mở rộng kết nối ra miền Trung', 'tin3.jpg', 'Dịch Covid-19 ở Việt Nam đang dần được kiểm soát, nhiều tỉnh, thành phố đã nới lỏng giãn các biện pháp và triển khai mở cửa du lịch đón khách trở lại. Tiếp nối thành công của các tour du lịch gần như Củ Chi - Cần Giờ - Tây Ninh, TP. HCM mở thêm tour, tuyến và dịch vụ mới với tuyến du lịch miền Tây Nam Bộ, cùng với đó là du lịch mở rộng kết nối ra các tỉnh miền Trung vào tháng 11 này.', 'Theo tinh thần Nghị quyết 128, nhiều địa phương đã công bố là “vùng xanh”, là tiền đề quan trọng để ngành du lịch các tỉnh miền Trung triển khai lộ trình rõ ràng cho kế hoạch “du lịch xanh” đón khách trở lại. <br />\r\nhttps://media.vietravel.com/Images/NewsPicture/dl-tang-nhiet-mot.jpg\r\n<br />\r\nNếu như vào đầu tháng 10 chỉ có một số tour Cần Giờ - Củ Chi bằng đường bộ để tri ân các y, bác sĩ cùng thành phố chống dịch cũng như kích cầu du lịch "staycation" thì đến nay, Vietravel còn mang đến du khách thêm nhiều sản phẩm độc đáo như tour du lịch Cần Giờ 1 ngày bằng tàu cao tốc hay tour du lịch Tây Ninh từ 1 đến 2 ngày trải nghiệm cáp treo khám phá núi Bà Đen, phù hợp cho cả khách lẻ và nhóm khách. Song, cùng với việc nhiều địa phương nới lỏng giãn cách và mở lại nhiều đường bay nội địa, tháng 11 này, Vietravel mở thêm nhiều tour nội địa liên tuyến đến với các tỉnh miền Trung như Đà Nẵng, Hội An, Huế, Phan Thiết, Nha Trang, Qui Nhơn và Phú Yên với hành trình tour trọn gói từ 3 ngày 2 đêm đến 5 ngày 4 đêm cả về đường bộ lẫn đường hàng không với giá chỉ từ 2.890.000đ/Khách. Bên cạnh đó là các gói dịch vụ nghỉ dưỡng kết hợp phương tiện vận chuyển và dịch vụ phòng khách sạn giá hấp dẫn nhằm phục vụ tối đa nhu cầu khách hàng trong giai đoạn bình thường mới. \r\n <br />\r\nhttps://media.vietravel.com/Images/NewsPicture/dl-tang-nhiet-hai.jpg\r\n<br />\r\nĐể khởi động lại hoạt động du lịch trong điều kiện bình thường mới, các tỉnh miền Trung đã thiết lập “bong bóng an toàn” nối các vùng an toàn với nhau. Cụ thể, từ ngày 1-11, chương trình du lịch kết nối thành phố và từng tỉnh Khánh Hòa, Phú Yên và Bình Định đã được kết nối; trước đó, Thừa Thiên Huế - Ðà Nẵng - Quảng Nam đã chủ động phối hợp trong hợp tác phát triển du lịch. Các địa phương này đã ký văn bản liên kết phát triển du lịch với tên gọi "Ba địa phương - một điểm đến". \r\n <br />\r\nhttps://media.vietravel.com/Images/NewsPicture/dl-tang-nhiet-ba.jpg\r\n<br />\r\nHiện nay, nhiều đơn vị, doanh nghiệp du lịch đã hoàn thành việc xây dựng phương án phòng chống dịch COVID-19 sẵn sàng cho việc đón khách du lịch trở lại. Tại tỉnh Khánh Hoà, di tích Tháp Bà Ponagar, danh thắng Hòn Chồng đã mở cửa đón khách tham quan trở lại. Từ ngày 29/10, VinWonders Nha Trang cũng đã đón khách tham quan khu vui chơi giải trí vào 3 ngày cuối tuần. Còn tại Thừa Thiên Huế, hoạt động du lịch đang dần được “hâm nóng”. Các điểm di tích Đại Nội, lăng vua Minh Mạng, lăng vua Tự Đức, lăng vua Khải Định… đã mở cửa đón khách. Nhằm đảm bảo các điều kiện phòng chống dịch bệnh COVID-19, Trung tâm Bảo tồn di tích Cố đô Huế đã hoàn thành việc lắp đặt hệ thống quét mã QR tại các điểm tham quan. ', 11112, '2021-11-05', 1, 3, 0),
(4, 'Du lịch an toàn với “thẻ xanh covid-19”', 'tin4.jpg', 'Trong bối cảnh Covid-19 cơ bản đã được kiểm soát, người dân cũng đang dần hòa nhập với cuộc sống “bình thường mới” và sẵn sàng lên kế hoạch xả stress sau những ngày “chôn chân” quá lâu vì giãn cách. Với các tín đồ du lịch có thẻ xanh Covid-19 yêu thích khám phá… sẽ an tâm vì Vietravel giúp bạn thực hiện chuyến đi đến những vùng xanh một cách tiết kiệm, đảm bảo an toàn, tiếp thêm nguồn năng lượng mới.', '<p><b>Sản phẩm du lịch khép kín đảm bảo an toàn</b></p>\r\n<p>Ngày 11/10 vừa qua, Chính phủ đã ban hành Nghị quyết 128/NQ-CP về việc “Thích ứng an toàn, linh hoạt, kiểm soát hiệu quả dịch Covid-19”. Theo đó các Bộ ngành, địa phương lần lượt đưa ra các hướng dẫn tạm thời thực hiện nghị quyết trên với mục tiêu dần mở cửa phục hồi kinh tế nói chung và du lịch nói riêng theo hướng thích ứng với trạng thái “bình thường mới”.\r\nNhằm tái khởi động lại sau giai đoạn giãn cách lần thứ 4, Vietravel mang đến du khách nhiều sản phẩm du lịch khép kín, điểm đến an toàn, bình ổn giá cùng nhiều ưu đãi hấp dẫn. Sản phẩm du lịch theo mô hình kép kín đang là xu hướng do đáp ứng các tiêu chí an toàn và đảm bảo các nguyên tắc giãn cách xã hội, giúp du khách vượt qua tâm lý “sợ dịch”. Mô hình này chú trọng khâu an toàn ở cả 3 thành tố là du khách, công ty lữ hành và các dịch vụ tổ hợp khác. Theo đó, du khách và nhân viên công ty đều phải được phải tiêm chủng vaccine đầy đủ, xét nghiệm âm tính, tuân thủ 5K khi tham quan, ăn uống, nghỉ dưỡng. Ngoài ra, điểm đến cũng được kiểm tra và đảm bảo an toàn…</p>\r\n<p>\r\n<img src="https://media.vietravel.com/Images/NewsPicture/du-lich-an-toan.jpg" />\r\n</p>\r\n<p>Hiện tại, Vietravel đang tung ra một số sản phẩm du lịch “vùng xanh”, cụ thể các tour gần khởi hành từ TP.HCM gồm: Cần Giờ, Củ Chi, Tây Ninh, Củ Chi - Tây Ninh, Bình Châu... Giai đoạn kế tiếp Công ty sẽ tổ chức các hành trình về miền Tây, Phú Quốc, đến miền Bắc với Sapa, Hạ Long, Sapa - Hạ Long, Đông Bắc, Tây Bắc; hay Nam Trung Bộ với Quy Nhơn, Phú Yên - Quy Nhơn, Mũi Né, Ninh Chữ, Phan Thiết - Nha Trang cùng hàng loạt Gói dịch vụ du lịch nghỉ dưỡng kết hợp phương tiện vận chuyển và dịch vụ phòng khách sạn nhằm phục vụ tối đa nhu cầu khách hàng trong giai đoạn bình thường mới.</p>\r\n<p>Bên cạnh đó, Vietravel cũng đang triển khai sản phẩm hồi hương đón người Việt về nước hay các chuyên gia đến Việt Nam làm việc. Và các dịch vụ: vé máy bay, xin visa, du học, vận chuyển Asia Express… cũng đáp ứng được nhu cầu cần thiết của du khách sau dịch.</p>\r\n<p><img src="https://media.vietravel.com/Images/NewsPicture/can-gio_7.jpg"/></p>\r\n<p>Sôi động và an toàn với “Hành trình xanh, an tâm trải nghiệm” Nhằm gia tăng nhiều giá trị, tiện ích, giúp khách hàng tiết kiệm chi phí khi tham gia tour, Vietravel tung ra chương trình khuyến mại “Sôi động và An toàn - Hành trình xanh, an tâm trải nghiệm” với hàng loạt ưu đãi hấp dẫn như sau: + “Chớp deal” với 4.200 E-voucher du lịch bằng ví MoMo Để thỏa đam mê du lịch của du khách, Vietravel kết hợp với ví điện tử MoMo tung ra 4.200 E-voucher mệnh giá 200.000 đồng được bán chỉ với 49.000 đồng/voucher trên chuyên mục Chợ Deal. Thời gian “săn deal” diễn ra khung giờ từ 20h - 22h tối thứ 5 hàng tuần từ 22/10/2021 - 22/01/2022 và hạn sử dụng E-Voucher đến hết ngày 15/03/2022. Chương trình áp dụng cho du khách đặt mua tour trọn gói, gói dịch vụ lẻ tại các văn phòng của Vietravel ở TP.HCM và Hà Nội hoặc đặt mua online qua ứng dụng Vietravel App, website: www.travel.com.vn. Mỗi E-Voucher chỉ áp dụng cho 01 lần thanh toán với hoá đơn từ 1.000.000 đồng trở lên và không áp dụng đồng thời với các ưu đãi khác. + Ưu đãi nhóm “Đi càng đông, giảm càng nhiều” Vietravel đặc biệt xây dựng chính sách ưu đãi hấp dẫn dành khách hàng đi theo nhóm, vừa tiết kiệm được một khoản chi phí vừa góp thêm niềm vui trong mỗi chuyến đi. Theo đó, từ 15/11 - 15/12, khách hàng mới khi mua tour theo nhóm từ 3-5 người sẽ được giảm tối đa đến 150.000đ/người; từ 6 người trở lên sẽ được giảm tối đa đến 200.000đ/người. Đối với khách hàng là Hội viên VietravelPlus khi mua tour từ 3-5 người sẽ được giảm tối đa đến 170.000đ/người, từ 6 người trở lên sẽ được giảm tối đa đến 220.000đ/người. Đặc biệt, nhóm từ 3-5 người có khách là trẻ em dưới 12 tuổi đi cùng sẽ được miễn phí 01 vé trẻ em, từ 6 người trở lên sẽ được miễn phí 02 vé trẻ em, áp dụng cho các tour đi bằng xe.</p>\r\n<p><img src="https://media.vietravel.com/Images/NewsPicture/Vietravel%20-Quan%20Ba%20-%20resize.jpg"/></p>\r\n<p>Xin cảm ơn</p>', 12240, '2021-11-06', 1, 1, 0),
(5, 'Liên kết phát triển giúp phục hồi nhanh chóng Du lịch TP.HCM và 13 tỉnh, thành Đồng bằng sông Cửu Lo', 'tin5.jpeg', 'Ngày 30-31/10/2021 đã diễn ra sự kiện Liên kết phát triển Du lịch giữa TP.Hồ Chí Minh và hai tỉnh Bến Tre, Long An. Đây là chuỗi các hoạt động trong khuôn khổ Liên kết phát triển Du lịch TP.HCM và 13 tỉnh, thành Đồng bằng sông Cửu Long (ĐBSCL). Chương trình có sự tham dự của Lãnh đạo Ủy ban nhân dân thành phố, Sở Du lịch, Sở Giao thông vận tải, Sở Công thương, Hiệp hội Du lịch TP.HCM, cùng các doanh nghiệp lữ hành và cơ quan thông tấn báo chí.', 'Ngày 30-31/10/2021 đã diễn ra sự kiện Liên kết phát triển Du lịch giữa TP.Hồ Chí Minh và hai tỉnh Bến Tre, Long An. Đây là chuỗi các hoạt động trong khuôn khổ Liên kết phát triển Du lịch TP.HCM và 13 tỉnh, thành Đồng bằng sông Cửu Long (ĐBSCL). Chương trình có sự tham dự của Lãnh đạo Ủy ban nhân dân thành phố, Sở Du lịch, Sở Giao thông vận tải, Sở Công thương, Hiệp hội Du lịch TP.HCM, cùng các doanh nghiệp lữ hành và cơ quan thông tấn báo chí.<br />\r\n\r\nTại Việt Nam, dịch Covid-19 đang dần được kiểm soát, một số tỉnh, thành phố đã nới lỏng các biện pháp giãn cách xã hội. Nhiều địa phương triển khai “Thích ứng an toàn, linh hoạt, kiểm soát hiệu quả dịch Covid-19” theo Nghị quyết số 128/NQ-CP ngày 11/10/2021 của Chính phủ nên hoạt động thương mại trong nước và vận tải dần khôi phục trở lại ngay trong tháng 10/2021. Ngành Du lịch cả nước nói chung và TP.HCM nói riêng cũng đã nhanh chóng triển khai các chương trình xúc tiến phát triển du lịch liên vùng, để kịp thời vực dậy ngành du lịch trong nước, tạo đà cạnh tranh với các nước trong khu vực.<br />\r\nhttps://media.vietravel.com/Images/NewsPicture/mien-tay-du-lich.jpeg\r\n<br />\r\nKhông chỉ sở hữu các điểm đến có thể tiếp cận thuận tiện bằng đường bộ từ TP.Hồ Chí Minh thông qua các tuyến cao tốc đang được triển khai như đoạn Trung Lương - Mỹ Thuận, và ở giai đoạn sau sẽ kết nối đến Cần Thơ, giúp rút ngắn thời gian di chuyển từ TP.Hồ Chí Minh đến Cần Thơ chỉ còn hai giờ. Về đường bay, trước khi đại dịch Covid-19 diễn ra, cảng hàng không Tân Sơn Nhất đón hơn 7,7 triệu lượt khách quốc tế đến (2019), đây cũng là một nguồn khách tiềm năng cho khu vực ĐBSCL. Bên cạnh 10 đường bay nội địa, sân bay quốc tế Cần Thơ còn phục vụ 4 đường bay quốc tế kết nối trực tiếp với các cửa ngõ giao thông của khu vực gồm Bangkok, Kuala Lumpur, Seoul và Đài Bắc.\r\n<br />\r\nVì vậy, việc triển khai các dự án cải thiện hạ tầng giao thông sẽ giúp khu vực ĐBSCL mở rộng khả năng tiếp cận thị trường khách nội địa và quốc tế, từ đó nâng cao sức cạnh tranh của vùng trên bản đồ du lịch Việt Nam. Khi phát triển liên kết vùng giữa các tỉnh, thành với TP.Hồ Chí Minh, các địa phương sẽ có sự hỗ trợ trong việc khai thác và đáp ứng tối đa nhu cầu du lịch, mang đến những trải nghiệm dài ngày, đa dạng hơn cho du khách.\r\n <br />\r\nhttps://media.vietravel.com/Images/NewsPicture/dong-bang-song-cuu-long.jpg\r\n<br />\r\nTiến xa hơn nữa, bằng cách xây dựng và nâng cao khả năng hợp tác và liên kết giữa các công ty lữ hành, các đơn vị cung ứng dịch vụ nhà hàng-khách sạn và các hoạt động giải trí trên sông như Yatch, Cruise ship, từ đó xây dựng hình ảnh và tăng cường các hoạt động truyền thông; sẽ giúp kết nối các điểm tham quan trong khu vực không chỉ đơn thuần là điểm đến đơn lẻ mà là một phần của hành trình khám phá vùng đất phía Nam cho du khách trong và ngoài nước khi du lịch hồi phục hoàn toàn.', 1014, '2021-11-07', 1, 1, 0),
(6, 'Đảo du lịch Bali giới hạn du khách nước ngoài dịp lễ cuối năm', 'ttdl.jpg', '    Chính quyền tỉnh Bali lên kế hoạch giới hạn lượng khách nước ngoài đổ về trong kỳ nghỉ Giáng Sinh và mừng năm mới sắp tới. \r\nÔng Tjok Bagus Pemayun, quyền Giám đốc Cơ quan Du lịch Bali cho biết, việc giới hạn du khách nước ngoài đến Bali trong kỳ nghỉ lễ sắp tới nhằm tránh sự lây lan của các biển thể mới virus SARS-CoV-2, có thể “nhấn chìm” Indonesia trong “làn sóng” Covid-19 lần thứ ba. Theo kế hoạch, Bali sẽ chỉ chấp nhận đón tối đa mỗi ngày 1.500 du khách nước ngoài trong mùa lễ Giáng Sinh và Năm mới 2022.    ', '<p>Ch&iacute;nh quyền tỉnh Bali l&ecirc;n kế hoạch giới hạn lượng kh&aacute;ch nước ngo&agrave;i đổ về trong kỳ nghỉ Gi&aacute;ng Sinh v&agrave; mừng năm mới sắp tới. &nbsp;</p>\r\n\r\n<p><br />\r\n&Ocirc;ng Tjok Bagus Pemayun, quyền Gi&aacute;m đốc Cơ quan Du lịch Bali cho biết, việc giới hạn du kh&aacute;ch nước ngo&agrave;i đến Bali trong kỳ nghỉ lễ sắp tới nhằm tr&aacute;nh sự l&acirc;y lan của c&aacute;c biển thể mới virus SARS-CoV-2, c&oacute; thể &ldquo;nhấn ch&igrave;m&rdquo; Indonesia trong &ldquo;l&agrave;n s&oacute;ng&rdquo; Covid-19 lần thứ ba. Theo kế hoạch, Bali sẽ chỉ chấp nhận đ&oacute;n tối đa mỗi ng&agrave;y 1.500 du kh&aacute;ch nước ngo&agrave;i trong m&ugrave;a lễ Gi&aacute;ng Sinh v&agrave; Năm mới 2022.</p>\r\n\r\n<p><img alt="" src="https://dulichvn.org.vn/nhaptin/uploads/images/2021/Thang11/1911Dao-du-lich-Bali-gioi-han-du-khach-nuoc-ngoai-dip-le-cuoi-nam.jpeg" /></p>\r\n\r\n<p><em>Bali sẵn s&agrave;ng đ&oacute;n du kh&aacute;ch quốc tế (Ảnh: Reuters).</em></p>\r\n\r\n<p>Th&aacute;ng 10/2021, Thống đốc Bali Wayan Koster lạc quan cho biết, hơn 20.000 du kh&aacute;ch nước ngo&agrave;i đ&atilde; đặt trước ph&ograve;ng kh&aacute;ch sạn ở Bali cho c&aacute;c tour du lịch th&aacute;ng 11. Tuy nhi&ecirc;n, Hiệp hội Du lịch v&agrave; Lữ h&agrave;nh Indonesia (ASITA) cho rằng, số lượng đặt ph&ograve;ng trước kh&ocirc;ng mấy &yacute; nghĩa bởi người du lịch c&oacute; thể hủy ph&ograve;ng bất cứ l&uacute;c n&agrave;o.</p>\r\n\r\n<p>Thực tế, t&iacute;nh đến đầu tuần n&agrave;y, sau hơn một th&aacute;ng mở cửa trở lại, vẫn chưa c&oacute; chuyến bay quốc tế n&agrave;o đ&aacute;p xuống s&acirc;n bay quốc tế Ngurah Rai ở Bali. Trong khi đ&oacute;, lượng kh&aacute;ch nội địa đến đảo du lịch Bali tăng mạnh, đạt khoảng 10.000 người mỗi ng&agrave;y.</p>\r\n\r\n<p>C&aacute;c h&atilde;ng du lịch Indonesia cho biết, nhiều du kh&aacute;ch nước ngo&agrave;i đang chọn đến Th&aacute;i Lan thay v&igrave; Bali, do Th&aacute;i Lan kh&ocirc;ng c&aacute;ch ly bắt buộc c&aacute;c du kh&aacute;ch đ&atilde; ti&ecirc;m đầy đủ vaccine ngừa Covid-19.</p>\r\n\r\n<p>Trong khi đ&oacute;, ch&iacute;nh phủ Indonesia vẫn y&ecirc;u cầu kh&aacute;ch nước ngo&agrave;i phải trải qua 03 ng&agrave;y c&aacute;ch ly bắt buộc. Thống đốc Bali đang k&ecirc;u gọi ch&iacute;nh phủ Indonesia giảm số ng&agrave;y c&aacute;ch ly cho du kh&aacute;ch nước ngo&agrave;i xuống c&ograve;n 01 ng&agrave;y.</p>\r\n\r\n<p>Để tr&aacute;nh dịch bệnh Covid-19 b&ugrave;ng ph&aacute;t lại, Bali đang thắt chặt c&aacute;c quy định y tế CHSE (Vệ sinh, Sức khỏe, An to&agrave;n v&agrave; Bền vững M&ocirc;i trường) ở nhiều kh&aacute;ch sạn, điểm du lịch. C&aacute;c c&acirc;u lạc bộ giải tr&iacute; về đ&ecirc;m tại Bali bị cấm tổ chức c&aacute;c buổi tiệc quy m&ocirc; lớn, trong khi kh&aacute;ch sạn cũng kh&ocirc;ng được cấp ph&eacute;p tổ chức c&aacute;c tiệc cưới hoặc sự kiện lớn./.</p>\r\n', 6, '2021-11-19', 1, 0, 0),
(8, 'Hành trình khám phá địa điểm du lịch phía Bắc mùa Đông lý tưởng', 'hinhtin.jpg', '         Tháng 11 12 du lịch miền Bắc nên đi đâu” hay “du lịch miền Bắc có gì” là câu hỏi được rất nhiều người đặt ra, bởi không phải mùa đông ở miền Bắc thiếu địa điểm để đi chơi mà là vì có quá nhiều sự lựa chọn, quá nhiều cảnh đẹp để tham quan khiến người ta phải phân vân, khó chọn lựa. Hãy để Vietravel bật mí cho bạn nhé!         ', '<p>Th&aacute;ng 11 12 du lịch miền Bắc n&ecirc;n đi đ&acirc;u&rdquo; hay &ldquo;du lịch miền Bắc c&oacute; g&igrave;&rdquo; l&agrave; c&acirc;u hỏi được rất nhiều người đặt ra, bởi kh&ocirc;ng phải m&ugrave;a đ&ocirc;ng ở miền Bắc thiếu địa điểm để đi chơi m&agrave; l&agrave; v&igrave; c&oacute; qu&aacute; nhiều sự lựa chọn, qu&aacute; nhiều cảnh đẹp để tham quan khiến người ta phải ph&acirc;n v&acirc;n, kh&oacute; chọn lựa. H&atilde;y để Vietravel bật m&iacute; cho bạn nh&eacute;!</p>\r\n\r\n<p>Việt Nam m&igrave;nh đẹp lắm, khắp 3 miền mỗi nơi một vẻ &ldquo;mười ph&acirc;n vẹn mười&rdquo;, thế n&ecirc;n d&ugrave; &ldquo;gu&rdquo;&nbsp;<a href="https://travel.com.vn/" target="_blank">du lịch</a>&nbsp;của bạn l&agrave; g&igrave; cũng c&oacute; nơi d&agrave;nh ri&ecirc;ng cho bạn. Cuối th&aacute;ng 11 khi miền Nam bước v&agrave;o giai đoạn kết th&uacute;c m&ugrave;a nước nổi v&agrave; miền Trung kh&ocirc;ng c&ograve;n những cơn mưa r&atilde; r&iacute;t, nắng đẹp chan h&ograve;a th&igrave; cũng l&agrave; l&uacute;c c&aacute;c tỉnh miền Bắc trở n&ecirc;n lạnh hơn bắt đầu đ&oacute;n ch&agrave;o những ng&agrave;y đ&ocirc;ng tuyệt đẹp.</p>\r\n\r\n<p><img alt="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" src="https://media.vietravel.com/Images/NewsPicture/moc-chau-MB.jpg" title="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" /></p>\r\n\r\n<p><br />\r\nDu lịch miền Bắc cuối năm l&agrave; dịp để bạn c&oacute; cơ hội được &ldquo;săn&rdquo; m&acirc;y trời bảng lảng, ngắm những lo&agrave;i hoa dại nhưng đầy hương sắc khiến bao người ngẩn ngơ, say đắm. V&agrave; nếu bạn c&oacute; dự định đi tour&nbsp;<a href="https://travel.com.vn/" target="_blank">du lịch</a>&nbsp;miền Bắc th&aacute;ng 11 n&agrave;y th&igrave; nhớ về T&acirc;y Bắc nh&eacute;. Du lịch T&acirc;y Bắc th&aacute;ng 11 đến Mộc Ch&acirc;u sẽ l&agrave; trải nghiệm tuyệt vời với những ai y&ecirc;u lo&agrave;i hoa cải trắng. L&uacute;c n&agrave;y ở tại c&aacute;c bản Ba Ph&aacute;ch 1, 2, 3, Bản &Aacute;ng v&agrave; khu vực Ngũ Động Bản &Ocirc;n đương v&agrave;o m&ugrave;a đẹp nhất, xinh xắn tựa chốn bồng lai ti&ecirc;n cảnh. Ngo&agrave;i ra, bạn c&ograve;n c&oacute; thể thỏa sức chi&ecirc;m ngưỡng v&agrave; chụp ảnh tại c&aacute;c địa điểm du lịch Mộc Ch&acirc;u nổi tiếng như kh&aacute;c như: th&aacute;c Dải Yếm, đồi ch&egrave; tr&aacute;i tim, vườn hoa Happy Land&hellip;<br />\r\n<br />\r\nTham khảo th&ecirc;m:&nbsp;<strong>Tour du lịch&nbsp;<a href="https://travel.com.vn/tim-tour/NDSGN178/ket-qua-tim-kiem.aspx">miền bắc 5 ng&agrave;y 4 đ&ecirc;m kh&aacute;m ph&aacute; Mộc Ch&acirc;u &ndash; Mai Ch&acirc;u</a></strong><a href="https://travel.com.vn/tim-tour/NDSGN178/ket-qua-tim-kiem.aspx">.</a><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" src="https://media.vietravel.com/Images/NewsPicture/dien-bien-MB.jpg" title="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" /></p>\r\n\r\n<p>Ảnh: Viettourist</p>\r\n\r\n<p><br />\r\nVới kh&ocirc;ng kh&iacute; trong l&agrave;nh, khung cảnh thi&ecirc;n nhi&ecirc;n k&igrave; vĩ c&ugrave;ng thảm thực vật phong ph&uacute;, hồ P&aacute; Khoang đang l&agrave; điểm&nbsp;<a href="https://travel.com.vn/" target="_blank">du lịch</a>&nbsp;thu h&uacute;t đ&ocirc;ng đảo du kh&aacute;ch ở Điện Bi&ecirc;n. Đặc biệt tr&ecirc;n h&ograve;n đảo nhỏ ở giữa hồ P&aacute; Khoang rộng h&agrave;ng chục ha c&ograve;n c&oacute; một &ldquo;Nhật Bản thu nhỏ&rdquo; với hơn 400 c&acirc;y&nbsp;<a href="https://travel.com.vn/tim-tour/hoa%20anh%20%C4%91%C3%A0o/ket-qua-tim-kiem.aspx?statusSeat=1&amp;utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank" title="hoa anh đào">hoa anh đ&agrave;o</a>&nbsp;nở rực rỡ giữa n&uacute;i rừng T&acirc;y Bắc từ th&aacute;ng 12. Tại đ&acirc;y, bạn c&ograve;n c&oacute; cơ hội được kh&aacute;m ph&aacute;, trải nghiệm c&aacute;c phong tục tập qu&aacute;n của d&acirc;n tộc người Th&aacute;i, Khơ M&uacute; v&agrave; hưởng thức những đặc sản mang đậm hương vị n&uacute;i đặc trưng như c&aacute; xi&ecirc;n nướng, thịt hun kh&oacute;i, cơm lam, m&oacute;n x&ocirc;i đồ đựng trong co&oacute;ng hay những ch&eacute; rượu thơm nồng.<br />\r\n<br />\r\nTham khảo th&ecirc;m:&nbsp;<strong>Tour du lịch&nbsp;<a href="https://travel.com.vn/tim-tour/NDSGN176/ket-qua-tim-kiem.aspx">miền bắc 3 ng&agrave;y 2 đ&ecirc;m kh&aacute;m ph&aacute; Điện Bi&ecirc;n.</a></strong>&nbsp;<br />\r\n&nbsp;<img alt="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" src="https://media.vietravel.com/Images/NewsPicture/ha-giang-MB.jpg" title="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" /></p>\r\n\r\n<p><br />\r\nDu lịch miền Đ&ocirc;ng Bắc m&ugrave;a n&agrave;y cũng kh&ocirc;ng k&eacute;m phần th&uacute; vị đ&acirc;u nh&eacute;. Nếu hoa cải chưa đủ l&agrave;m bạn thỏa m&atilde;n, bạn c&oacute; h&atilde;y đến H&agrave; Giang để được &ldquo;say&rdquo; trong sắc phớt hồng của hoa tam gi&aacute;c mạch. Đ&oacute; l&agrave; chưa kể, H&agrave; Giang sở hữu rất nhiều thắng cảnh đẹp, thi&ecirc;ng li&ecirc;ng v&agrave; b&iacute; ẩn như phố cổ Đồng Văn, Ph&oacute; Bảng, cột cờ Lũng C&uacute;, cao nguy&ecirc;n đ&aacute; Đồng Văn, đ&egrave;o M&atilde; P&iacute; L&egrave;ng, chợ phi&ecirc;n H&agrave; Giang, Ho&agrave;ng Phu S&igrave; hay n&uacute;i Cấm... Mỗi thửa đất, mỗi con người H&agrave; Giang v&agrave; nếp sống của họ lại l&agrave; một c&acirc;u chuyện văn h&oacute;a đặc sắc khiến bạn sẽ th&iacute;ch m&ecirc;. Đi tour miền Bắc th&aacute;ng 11 n&agrave;y, nhất định đừng lỡ hẹn với&nbsp;<a href="https://travel.com.vn/" target="_blank">du lịch</a>&nbsp;H&agrave; Giang nh&eacute;.<br />\r\n<br />\r\nTham khảo th&ecirc;m:&nbsp;<strong>Tour du lịch&nbsp;<a href="https://travel.com.vn/tim-tour/NDSGN157/ket-qua-tim-kiem.aspx">miền bắc 5 ng&agrave;y 4 đ&ecirc;m kh&aacute;m ph&aacute; H&agrave; Giang.</a>&nbsp;</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" src="https://media.vietravel.com/Images/NewsPicture/cao-bang-MB.jpg" title="Hành trình khám phá địa điểm du lịch Miền Bắc mùa đông lý tưởng" /></p>\r\n\r\n<p>Ảnh: Trần Bảo Ho&agrave;</p>\r\n\r\n<p><br />\r\nNếu bạn muốn ngắm hoa, chụp ảnh tham quan th&igrave; đi tour miền Bắc th&aacute;ng 11 đi Cao Bằng ch&iacute;nh l&agrave; sự lựa chọn tuyệt vời. Đ&acirc;y l&agrave; thời điểm nở rộ đẹp nhất của đồi hoa d&atilde; quỳ. Sắc v&agrave;ng của d&atilde; quỳ tr&ecirc;n v&ugrave;ng cao non nước nơi n&agrave;y khiến nhiều tr&aacute;i tim du kh&aacute;ch phải thổn thức v&agrave; kh&oacute; l&ograve;ng qu&ecirc;n được c&aacute;i cảm gi&aacute;c được ch&igrave;m đắm trong khung cảnh tuyệt đẹp đ&oacute;. Từng kh&oacute;m hoa d&atilde; quỳ ở dọc theo đường đ&egrave;o c&agrave;ng khiến khung cảnh cheo leo, hiểm trở nơi đ&acirc;y trở n&ecirc;n thơ mộng hơn. Kh&ocirc;ng những thế, Th&aacute;c Bản Giốc, Hồ Thang Hen thời điểm n&agrave;y cũng đẹp v&ocirc; c&ugrave;ng, nước chảy rất nhẹ nh&agrave;ng tạo n&ecirc;n kh&ocirc;ng gian thật l&atilde;ng mạn, n&ecirc;n thơ.&nbsp;<br />\r\n<br />\r\nTham khảo th&ecirc;m:&nbsp;<strong>Tour&nbsp;<a href="https://travel.com.vn/" target="_blank">du lịch</a>&nbsp;<a href="https://travel.com.vn/tim-tour/NSGN150/ket-qua-tim-kiem.aspx">miền bắc 4 ng&agrave;y 3 đ&ecirc;m kh&aacute;m ph&aacute; Cao Bằng.</a></strong><br />\r\n<br />\r\nV&agrave; nếu đ&atilde; lỡ đắm say v&ugrave;ng Đ&ocirc;ng Bắc chẳng nỡ &ldquo;rời ch&acirc;n&rdquo;, bạn cũng c&oacute; thể n&aacute;n lại đ&ocirc;i ba h&ocirc;m với h&agrave;nh tr&igrave;nh&nbsp;<a href="https://travel.com.vn/tim-tour/NSGN1522/ket-qua-tim-kiem.aspx">tour du lịch miền Bắc 6 ng&agrave;y 5 đ&ecirc;m</a>&nbsp;khởi h&agrave;nh từ TP HCM để c&oacute; thể đi được nhiều nơi hơn, kh&aacute;m ph&aacute; nhiều hơn v&agrave; chụp thật nhiều bức ảnh &ldquo;lưu giữ thanh xu&acirc;n&rdquo; c&ugrave;ng m&ugrave;a đ&ocirc;ng miền Bắc.</p>\r\n', 5, '2021-11-19', 1, 0, 0);

