--
-- Table structure for table `tour`
--

CREATE TABLE IF NOT EXISTS `tour` (
  `id_tour` int(11) NOT NULL AUTO_INCREMENT,
  `ten` varchar(100) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `tomTat` text COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `hinh1` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `hinh2` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `hinh3` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `hinh4` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `lichTrinh` text COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `ngay` date NOT NULL,
  `thoiGian` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `xuatPhat` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `soLuongNguoi` int(11) NOT NULL,
  `gia` float NOT NULL,
  `trangThai` tinyint(4) NOT NULL DEFAULT '1',
  `id_theLoai` int(11) NOT NULL,
  `id_taiKhoan` int(11) NOT NULL,
  PRIMARY KEY (`id_tour`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_vietnamese_ci AUTO_INCREMENT=16 ;

--
-- Dumping data for table `tour`
--

INSERT INTO `tour` (`id_tour`, `ten`, `tomTat`, `hinh1`, `hinh2`, `hinh3`, `hinh4`, `lichTrinh`, `ngay`, `thoiGian`, `xuatPhat`, `soLuongNguoi`, `gia`, `trangThai`, `id_theLoai`, `id_taiKhoan`) VALUES
(1, 'du lịch Hà Nội', '', 'hanoi1.jpg', 'hanoi2.jpg', 'hanoi3.jpg', 'hanoi4.jpg', 'Ngày 1 - TP. HỒ CHÍ MINH - NỘI BÀI (HÀ NỘI) - LÀO CAI – SAPA (Ăn trưa, chiều)<br />\r\nQuý khách tập trung tại sân bay Tân Sơn Nhất - ga đi trong nước. Hướng dẫn viên làm thủ tục cho đoàn đáp chuyến bay đi Hà Nội.<br />\r\nXe Vietravel đón đoàn tại sân bay Nội Bài, khởi hành đi Sa Pa theo cung đường cao tốc hiện đại và dài nhất Việt Nam. Trên đường dừng tham quan Đền Quốc Tổ Lạc Long Quân được tọa lạc trên đồi Sim có hình thể giống một con rùa lớn, hai bên có Thanh Long, Bạch Hổ, phía trước là hồ Hóc Trai và sông Hồng chảy xuôi về biển, biểu hiện sự linh thiêng, huyền bí với thế "sơn chầu thủy tụ" vô cùng đắc địa.\r\nĐến Sapa Quý khách tham quan:\r\n- Bản Cát Cát đẹp như một bức tranh giữa vùng phố cổ Sapa, nơi đây thu hút du khách bởi cầu treo, thác nước, guồng nước và những mảng màu hoa mê hoặc du khách khi lạc bước đến đây.<br />\r\n- Thăm những nếp nhà của người Mông, Dao, Giáy trong bản, du khách sẽ không khỏi ngỡ ngàng trước vẻ đẹp bình dị, mộc mạc. <br />\r\nBuổi tối Quý khách dạo phố, ngắm nhà thờ Đá Sapa, tự do thưởng thức đặc sản vùng cao như: thịt lợn cấp nách nướng, trứng nướng, rượu táo mèo, giao lưu với người dân tộc vùng cao. <br />\r\nNghỉ đêm tại Sapa.', '2021-11-08', '1 ngày 1 đêm', 'thủ dô Hà Nội', 5, 11000000, 1, 1, 1),
(2, 'du lịch Càu Vàng - Đà Nẵng', '', '2-tour-1.jpg', '2-tour-2.jpg', '2-tour-3.jpg', '2-tour-4.jpg', 'Ngày 1 - TP.HỒ CHÍ MINH - ĐÀ NẴNG - HỘI AN Số bữa ăn: 02 (Ăn trưa, tối)<br />\r\nQuý khách tập trung tại điểm hẹn, Ga đi trong nước, sân bay Tân Sơn Nhất. Hướng dẫn viên Vietravel hỗ trợ làm thủ tục cho đoàn đáp chuyến bay đi Đà Nẵng. Tại sân bay Đà Nẵng xe và HDV Vietravel đón đoàn đi tham quan: <br />\r\n-  Bán đảo Sơn Trà - viếng Chùa Linh Ứng: Nơi đây có tượng Phật Quan Thế Âm cao nhất Việt Nam, đứng nơi đây, Quý khách sẽ được chiêm ngưỡng toàn cảnh thành phố, núi rừng và biển đảo Sơn Trà một cách hoàn hảo nhất. <br />\r\n-  Phố Cổ Hội An: Chùa Cầu, Nhà Cổ Phùng Hưng, Hội Quán Phước Kiến, Cơ sở Thủ Công Mỹ Nghệ,…Quý khách tự do dạo phố đèn lồng đầy màu sắc, cảm nhận sự yên bình cổ kính và lãng mạn Phố Cổ về đêm,... Đoàn quay về Đà Nẵng, buổi tối quý khách tự do dạo phố ngắm nhìn sự lung linh và phát triển của thành phố …. Nghỉ đêm tại Đà Nẵng. <br />', '2021-11-08', '1 ngày', 'tp Đà Nẵng', 9, 9000000, 0, 1, 1),
(3, 'Củ Chi - Nông Trang Xanh - Địa Đạo vùng Đất Thép', 'Hành trình đưa Quý khách đến Nông Trang Xanh trở về với thôn quê mộc mạc, tìm hiểu quy trình trồng nấm sạch, thử tài khéo léo vắt sữa bò hay ghé vườn rau hữu cơ để biết rõ hơn quy trình trồng rau theo công nghệ sinh học, không có hóa chất và thuốc trừ sâu. Thăm “vùng đất thép” Củ Chi - Nơi thu nhỏ trận đồ biến hóa và sáng tạo của quân và dân Củ Chi trong cuộc kháng chiến giành tự do cho Tổ quốc.', '3-tour-1.jpg', '3-tour-2.jpg', '3-tour-3.jpg', '3-tour-4.jpg', 'Ngày 1 - NÔNG TRANG XANH - ĐỊA ĐẠO CỦ CHI Số bữa ăn: 1 bữa ( trưa)<br />\r\nQuý khách tập trung tại Vietravel (190 Pasteur, Phường Võ Thị Sáu, Quận 3, TP.HCM) khởi hành đi Củ Chi - Một vùng đất kiên cường trong chiến tranh cách trung tâm TP. HCM khoảng 50 km. “Củ Chi đất thép” không chỉ nổi tiếng với chiến công lịch sử hào hùng, ngày nay Quý khách đến Củ Chi còn được trải nghiệm mô hình tiêu biểu cho sản xuất nông nghiệp sạch và du lịch sinh thái tại Nông Trang Xanh - Tìm về cảnh thôn quê mộc mạc:<br />\r\n\r\nKhám phá quy trình trồng nấm sạch: cũng như được tận tay tham gia thu hoạch nấm.\r\n<br />\r\nKhu chuồng trại chăn nuôi: tự tay hái lá cho dê, cừu ăn và thử tài khéo léo vắt sữa bò.<br />\r\n\r\nVườn rau hữu cơ sạch: áp dụng trồng theo công nghệ sinh học, không có hóa chất, thuốc trừ sâu.<br />\r\n\r\nLưu lại bức ảnh đáng nhớ trước vẻ đẹp của những vườn hoa sao nhái hoặc cánh đồng hoa hướng dương với sắc vàng rực rỡ đang thi nhau khoe sắc (tùy theo mùa) <br />\r\n\r\nTiếp tục hành trình, Quý khách khám phá một trong mười công trình ngầm ngoạn mục nhất thế giới - Địa Đạo Củ Chi với những trải nghiệm đặc biệt:<br />\r\n\r\nXem những thước phim tư liệu quý về vùng giải phóng những năm 60.<br />\r\n\r\nThử cảm giác chui địa đạo với độ dài 15m, lắng nghe các câu chuyện về cuộc sống người dân “Đất Thép” trong bom đạn (tùy thuộc vào tình hình thực tế).<br />\r\n\r\nĐặc biệt, du khách sẽ được tận mắt chứng kiến một phát minh hết sức sáng tạo trong việc giấu khói của bếp Hoàng Cầm và thưởng thức món ăn của du kích năm xưa mà giờ đây đã trở thành đặc sản: khoai mì chấm muối đậu.\r\n\r\nThăm Đền Bến Dược: nơi tưởng niệm những anh hùng chiến đấu đã hy sinh trên vùng đất Sài Gòn - Gia Định trong hai cuộc kháng chiến chống Pháp - Mỹ.<br />\r\n\r\nHòa mình vào không gian làng quê của khu tái hiện vùng giải phóng Củ Chi (1961 - 1972), xem Sa bàn và phim 3D mô phỏng diễn biến đánh bại trận càn Cedar Falls vào vùng Tam Giác Sắt Củ Chi<br />\r\n\r\nTham quan các mô hình trải nghiệm như: cấy lúa, giã gạo, bắt cá, xay lúa, đan lát, đặc biệt là thực hành nghề làm bánh tráng truyền thống của người dân Củ Chi.\r\n<br />\r\nTạm biệt Củ Chi, khởi hành về lại trung tâm TP. HCM, đoàn dừng chân Quán Vườn Cau - thưởng thức nước mía sầu riêng và củ mì hấp nước cốt dừa thơm ngon (tùy vào tình hình thực tế, chi phí nước uống tự túc).\r\n<br />\r\nXe đưa Quý khách về điểm đón ban đầu và kết thúc chương trình.', '2021-11-07', '2 ngày 1 đêm', 'Củ Chi', 7, 12000000, 1, 1, 2),
(5, 'Hành trình xanh Cần Giờ - Vàm Sát', 'Một ngày hòa minh giữa thiên nhiên xanh mát ở khu dự trữ sinh quyển thế giới, khám phá “ lá phổi xanh” của thành phố bằng các hoạt động tham quan tại Chiến Khu Rừng Sác, thưởng ngoạn cảnh quan sông nước hữu tình trên những chiếc ca nô khi len lỏi giữa khu rừng ngập mặn của KDL Dần Xây. Đặc biệt trong hành trình, quý khách còn được thưởng thức những món ăn đặc trưng chỉ có tại Vàm Sát – Cần Giờ như : vịt nước mặn, rau lìm kìm, cua hấp, tôm nướng, cá đối chiên xù cuốn bánh tráng, canh cá bui, chè dừa nước…Quay lại ký ức của Chiến khu xưa, nghe kể về dấu ấn lịch sử oai hùng một thời của lực lượng bộ đội đặc công rừng Sác.Trải nghiệm đi xuồng máy len lỏi vào rừng nguyên sinh ngập mặn. Hòa mình vào bức tranh thiên nhiên xanh mát ở Khu du lịch sinh thái Vàm Sát. Trải nghiệm ẩm thực đặc sắc, hấp dẫn và đặc trưng của Cần Giờ', '1637295040_nuocngoai4.jpg', '5-tour-2.jpg', '5-tour-3.jpg', '5-tour-4.jpg', '<p>Ng&agrave;y 1 - TRUNG T&Acirc;M TP.HCM &ndash; CẦN GIỜ &ndash; CĂN CỨ C&Aacute;CH MẠNG RỪNG S&Aacute;C &ndash; KHU DU LỊCH SINH TH&Aacute;I V&Agrave;M S&Aacute;T Số bữa ăn: 2 bữa (Ăn s&aacute;ng tr&ecirc;n xe, trưa)<br />\r\nXe v&agrave; Hướng dẫn vi&ecirc;n đ&oacute;n Qu&yacute; kh&aacute;ch tại văn ph&ograve;ng của Vietravel (190 Pasteur, Phường V&otilde; Thị S&aacute;u, Quận 3)<br />\r\nĐo&agrave;n khởi h&agrave;nh đi Cần Giờ - được mệnh danh l&agrave; &ldquo;l&aacute; phổi xanh&rdquo; của th&agrave;nh phố mang t&ecirc;n B&aacute;c v&agrave; d&ugrave;ng bữa s&aacute;ng tr&ecirc;n xe.<br />\r\nĐến KDL L&acirc;m Vi&ecirc;n Cần Giờ đo&agrave;n l&ecirc;n ca n&ocirc; thăm Chiến Khu Rừng S&aacute;c - nơi ghi lại dấu ấn lịch sử oai h&ugrave;ng một thời của c&aacute;c đặc c&ocirc;ng Rừng S&aacute;c trong thời kh&aacute;ng chiến chống Mỹ cứu nước từ năm 1966 đến 30-04-1975.<br />\r\nCano len lỏi v&agrave;o rừng nguy&ecirc;n sinh ngập mặn, đo&agrave;n đến v&ugrave;ng l&otilde;i của trung t&acirc;m Chiến Khu Rừng S&aacute;c: Du kh&aacute;ch như được sống lại trong khoảnh khắc oanh liệt của c&aacute;c chiến sĩ đo&agrave;n 10 - Đặc C&ocirc;ng Rừng S&aacute;c năm xưa qua c&aacute;c m&ocirc; h&igrave;nh v&ocirc; c&ugrave;ng sống động như h&igrave;nh ảnh chiến sĩ chiến đấu với c&aacute; sấu, bữa cơm đạm bạc hằng ng&agrave;y v&agrave; việc tự may tư trang cho m&igrave;nh của c&aacute;c chiến sĩ. B&ecirc;n cạnh đ&oacute;, c&aacute;c nh&agrave; th&ocirc;ng tin, xưởng qu&acirc;n giới, nh&agrave; qu&acirc;n y, nh&agrave; qu&acirc;n nhu&hellip; cũng được t&aacute;i hiện khắp nơi trong Chiến Khu Rừng S&aacute;c.<br />\r\nThắp hương tại tượng đ&agrave;i chiến sĩ đặc c&ocirc;ng Rừng S&aacute;c, nghe thuyết minh về một thời lịch sử oai h&ugrave;ng v&agrave; những đặc trưng của thủy triều ở v&ugrave;ng cửa s&ocirc;ng<br />\r\n10h00: Đo&agrave;n l&ecirc;n xe khởi h&agrave;nh đến Khu Du Lịch Sinh Th&aacute;i Dần X&acirc;y, đo&agrave;n l&ecirc;n ca n&ocirc; đến trung t&acirc;m Khu Du Lịch Sinh Th&aacute;i V&agrave;m S&aacute;t. Qu&yacute; kh&aacute;ch được dịp thưởng ngoạn cảnh quan s&ocirc;ng nước đặc trưng sinh th&aacute;i tại Cần Giờ; cũng như t&igrave;m hiểu c&aacute;c hoạt động đ&aacute;nh bắt thủy sản của ngư d&acirc;n Cần Giờ: thả lưới, đ&oacute;ng đ&aacute;y, nu&ocirc;i h&agrave;u,&hellip; (nếu c&oacute;). Đến nơi, xe điện đưa đo&agrave;n tham quan:<br />\r\nKhu Bảo tồn Chim: đ&atilde; được UBND TP.Hồ Ch&iacute; Minh c&ocirc;ng nhận v&agrave;o năm 2004 . Với số lượng tr&ecirc;n 20.000 con bao gồm 26 lo&agrave;i kh&aacute;c nhau, trong đ&oacute; c&oacute; một số lo&agrave;i nằm trong s&aacute;ch đỏ như: Giang Sen, Bồ N&ocirc;ng, Gi&agrave; Đẩy&hellip; Qu&yacute; kh&aacute;ch c&oacute; thể nh&igrave;n thấy sự n&aacute;o nhiệt của khu du lịch V&agrave;m S&aacute;t với h&agrave;ng ng&agrave;n c&aacute;nh chim nước rợp cả v&ugrave;ng trời (t&ugrave;y theo m&ugrave;a).<br />\r\nKhu bảo tồn c&aacute; sấu Hoa C&agrave;: t&igrave;m hiểu về quy tr&igrave;nh ấp trứng, nu&ocirc;i dưỡng v&agrave; bảo tồn c&aacute; sấu, đặc biệt l&agrave; tham gia tr&ograve; chơi cảm gi&aacute;c mạnh &ldquo;Du thuyền c&acirc;u C&aacute; Sấu&rdquo;.<br />\r\nNgắm nh&igrave;n đ&agrave;n khỉ đu&ocirc;i d&agrave;i sống hoang d&atilde; tự nhi&ecirc;n, rất tinh nghịch.<br />\r\nChinh phục Th&aacute;p Tang Bồng cao 26m: chi&ecirc;m ngưỡng vẻ đẹp to&agrave;n cảnh rừng ngập mặn từ tr&ecirc;n cao với cảnh sắc thi&ecirc;n nhi&ecirc;n đẹp như tranh vẽ.<br />\r\nKh&aacute;m ph&aacute; con đường b&ecirc; t&ocirc;ng xuy&ecirc;n rừng r&ecirc;u phong uốn lượn, c&acirc;y cối che phủ dọc hai b&ecirc;n đường rợp b&oacute;ng m&aacute;t, ph&iacute;a dưới l&agrave; hệ sinh th&aacute;i ngập nước với rễ c&acirc;y mọc tua tủa k&iacute;ch th&iacute;ch sự t&ograve; m&ograve; của Qu&yacute; kh&aacute;ch hay thử th&aacute;ch với chiếc cầu treo, tham quan đ&agrave;n Nai rừng hiền ho&agrave; th&acirc;n thiện,&hellip;<br />\r\nQu&yacute; kh&aacute;ch d&ugrave;ng cơm trưa trong Khu du lịch V&agrave;m S&aacute;t với c&aacute;c m&oacute;n ăn mang hương vị d&acirc;n d&atilde; của v&ugrave;ng Cần Giờ.<br />\r\nĐo&agrave;n di chuyển l&ecirc;n ca n&ocirc; qua Khu bảo tồn đ&agrave;n Dơi Nghệ: tại đ&acirc;y nh&acirc;n vi&ecirc;n ch&egrave;o xuồng tay sẽ đưa Qu&yacute; kh&aacute;ch len lỏi v&agrave;o rừng Đước trong đầm, Qu&yacute; kh&aacute;ch sẽ được ngắm nh&igrave;n một trong những giống dơi qu&yacute; hiếm nhất thế giới đang treo ngược m&igrave;nh tr&ecirc;n c&agrave;nh c&acirc;y v&agrave; tự do h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh, tho&aacute;ng m&aacute;t của v&ugrave;ng ngập mặn Cần Giờ.<br />\r\nĐo&agrave;n l&ecirc;n ca n&ocirc; trở lại KDL Dần X&acirc;y. Đo&agrave;n len lỏi qua con đường b&ecirc; t&ocirc;ng xuy&ecirc;n rừng v&agrave; được nghe giới thiệu về qu&aacute; tr&igrave;nh kh&ocirc;i phục v&agrave; ph&aacute;t triển hệ sinh th&aacute;i rừng ngập mặn Cần Giờ<br />\r\nQu&yacute; kh&aacute;chl&ecirc;n xe khởi h&agrave;nh về trung t&acirc;m TP. Hồ Ch&iacute; Minh. Đến điểm đ&oacute;n ban đầu, kết th&uacute;c chương tr&igrave;nh nhiều trải nghiệm.</p>\r\n', '2021-11-06', '3 ngày 2 đêm', 'Cần Giờ', 6, 1200000, 1, 1, 1),
(6, 'Huyền thoại Chăm Pa Mỹ Sơn - Bảo tàng Chăm ', 'Mỹ Sơn cách phố cổ Đà Nẵng 80 km về phía Tây Nam, là một khu đền tháp của triều Chămpa được xây dựng từ thế kỷ thứ 4 đến thế kỷ 12. Khu Tháp cổ Mỹ Sơn với hơn 70 công trình kiến trúc đền tháp rất độc đáo được xây dựng bằng gạch . Xin trân trọng giới thiệu chương trình tham quan này khám phá khu đền tháp Mỹ Sơn...', '6-tour-4.jpg', '6-tour-2.jpg', '6-tour-3.jpg', '6-tour-4.jpg', '<p>Ng&agrave;y 1 - Mỹ Sơn &ndash; Bảo T&agrave;ng Chăm (Ăn trưa)<br />\r\n8h30: Xe v&agrave; Hướng Dẫn Vi&ecirc;n đ&oacute;n Qu&yacute; kh&aacute;ch tại tại điểm hẹn, khởi h&agrave;nh đi Mỹ Sơn.<br />\r\n10h30: Xe đưa Qu&yacute; kh&aacute;ch t&igrave;m về với vẻ đẹp b&iacute; ẩn, huyền ảo của Di Sản Văn H&oacute;a Thế Giới. Qu&yacute; kh&aacute;ch c&oacute; cơ hội kh&aacute;m ph&aacute; Mỹ Sơn, tham quan Khu Th&aacute;nh Địa l&acirc;u đời của Vương Quốc Chăm Pa với những đền th&aacute;p r&ecirc;u phong b&iacute; ẩn từ thế kỉ VII, thưởng thức chương tr&igrave;nh ca m&uacute;a Chăm đặc sắc. Thăm Trung T&acirc;m Th&aacute;nh Mẫu Tr&agrave; Kiệu - đặt tr&ecirc;n nền m&oacute;ng Kinh Th&agrave;nh xưa của người Chăm (Kinh th&agrave;nh Sư Tử).<br />\r\n12h30: D&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng<br />\r\n13h30: Di chuyển đến Bảo t&agrave;ng Đi&ecirc;u Khắc Chăm - một bảo t&agrave;ng độc đ&aacute;o kh&ocirc;ng chỉ của th&agrave;nh phố Đ&agrave; Nẵng, Việt Nam m&agrave; c&ograve;n của cả thế giới. Ch&iacute;nh thức được x&acirc;y dựng v&agrave;o th&aacute;ng 7 năm 1915, hiện tại c&oacute; khoảng gần 2.000 hiện vật lớn nhỏ, trong đ&oacute; c&oacute; gần 500 hiện vật đang trưng b&agrave;y b&ecirc;n trong nh&agrave; Bảo t&agrave;ng. Hầu hết c&aacute;c t&aacute;c phẩm đi&ecirc;u khắc hiện c&oacute; tại bảo t&agrave;ng l&agrave; những t&aacute;c phẩm nguy&ecirc;n bản<br />\r\n15h30: Xe v&agrave; Hướng Dẫn Vi&ecirc;n đưa đo&agrave;n về điểm hẹn. Kết th&uacute;c chương tr&igrave;nh, hẹn gặp lại.<br />\r\nThời gian v&agrave; lộ tr&igrave;nh c&oacute; thể thay đổi phụ thuộc y&ecirc;u cầu của từng đo&agrave;n kh&aacute;ch hoặc c&aacute;c điều kiện kh&aacute;ch quan kh&aacute;c như đường x&aacute;, thời tiết, chuyến bay&hellip;!</p>\r\n', '0000-00-00', '1 ngày', 'tp Tam Kỳ', 4, 1000000, 1, 1, 2),
(7, 'Du lịch Mỹ liên tuyến Đông Tây lễ hội hoa anh đào 2020 từ Sài Gòn giá tốt', 'Đây là tour du lịch Mỹ mùa xuân Quý khách sẽ đi qua 6 thành phố nổi tiếng và thưởng ngoạn sắc hoa anh đào rực rỡ ở lễ hội hoa anh đào lớn nhất nước Mỹ:\r\n•    Thành phố New York hiện đại và năng động có Tượng nữ thần Tự do, Quảng trường thời đại, Phố Wall, Di tích Trung tâm thương mại Tháp đôi thế giới….\r\n•    Thành phố cố đô Philadelphia lịch sử, cổ kính và hoa lệ,\r\n•    Thủ đô Washington D.C với Nhà trắng, Quốc hội, Lầu năm góc, các bảo tàng nổi tiếng \r\n•    Thành phố không ngủ Las Vegas, thăm quan các casino hoành tráng nhất thế giới, Thủy điện Hoover Dam, Đại vực Grand Canyon \r\n•    Thành phố biển San Diego, phố cổ đặc trưng Tây Ban Nha\r\n•    Kinh đô điện ảnh thế giới Hollywood tại Los Angeles ', '1637745483_du-lich-he-my-du-lich-viet.jpg', '1637745483_dien-capital-du-lich-viet.jpg', '1637745483_tourdulich.jpg', '1637745483_tour-my-mua-xuan-du-lich-viet.jpg', '<p>NG&Agrave;Y 1 |&nbsp;H&Agrave; NỘI/TP.HCM - TRANSIT TAIPEI &ndash; NEW YORK (Ăn uống v&agrave; nghỉ ngơi tr&ecirc;n m&aacute;y bay)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n đo&agrave;n tập trung tại s&acirc;n bay v&agrave; l&agrave;m thủ tục h&agrave;ng kh&ocirc;ng, xuất cảnh. Qu&yacute; kh&aacute;ch đ&aacute;p m&aacute;y bay đi Taipei (thời gian bay 02h00). Đến Taipei, qu&yacute; kh&aacute;ch l&agrave;m thủ tục nối chuyến bay đi New york. Đo&agrave;n đ&aacute;p chuyến bay cất c&aacute;nh đưa Qu&yacute; kh&aacute;ch đến với Mỹ - đất nước gi&agrave;u mạnh nhất thế giới, niềm mơ ước của những cuộc đời muốn thay đổi. Do vượt tuyến đổi ng&agrave;y, đo&agrave;n đến New York c&ugrave;ng ng&agrave;y khởi h&agrave;nh v&agrave; ho&agrave;n tất thủ tục nhập cảnh, nhận h&agrave;nh l&iacute;. Xe đ&oacute;n đo&agrave;n về nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi sau chặng bay d&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;NEW YORK CITY TOUR (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn s&aacute;ng tại kh&aacute;ch sạn. Khởi hành tham quan Thành ph&ocirc;́ New York với những địa danh n&ocirc;̉i ti&ecirc;́ng:</p>\r\n\r\n<ul>\r\n	<li><strong>Tượng Nữ Th&acirc;̀n Tự Do&nbsp;</strong>&ndash; Biểu tượng của nước Mỹ bằng chương tr&igrave;nh du thuyền ra đảo nơi đặt bức tượng khổng lồ.</li>\r\n	<li>Đ&agrave;i tưởng niệm c&aacute;c nạn nh&acirc;n trong vụ khủng bố đẫm m&aacute;u &amp; Tàn tích của Trung t&acirc;m thương mại th&ecirc;́ giới sau sự ki&ecirc;̣n 11 tháng 9.</li>\r\n	<li><strong>Phố tài chính Wall Street&nbsp;</strong>&ndash; Nơi tọa lạc của sàn giao dịch chứng khoán New York và các trung t&acirc;m tài chính ng&acirc;n hàng. Nơi đ&acirc;y còn được xem là trung t&acirc;m tài chính của thế giới , mỗi chuyển đ&ocirc;̣ng của phố Wall ảnh hưởng đến tình hình tài chính toàn cầu.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng Việt Nam, sau đ&oacute; tiếp tục chương tr&igrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Trung t&acirc;m thương mại Rockerfeller&nbsp;</strong>&ndash; Trung t&acirc;m thương mại sầm u&acirc;́t, nơi đặt đại bản doanh của k&ecirc;nh truy&ecirc;̀n hình NBC, t&acirc;̣p trung các shop đ&ocirc;̀ hi&ecirc;̣u đắt ti&ecirc;̀n.</li>\r\n	<li><strong>Đại l&ocirc;̣ 5 Fifth avenue</strong>&nbsp;&ndash; Trung t&acirc;m thời trang của nước Mỹ, nơi t&acirc;̣p trung các sàn Catwalk và &nbsp;các thương hi&ecirc;̣u thời trang lớn của th&ecirc;́ giới.</li>\r\n	<li><strong>Quảng trường thời đại (Times Square)&nbsp;</strong>&ndash; Quảng trường trung t&acirc;m thành phố New York rực rỡ với những ánh đèn màu và bảng quảng cáo.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn tối tại nh&agrave; h&agrave;ng địa phương v&agrave; xe đưa đo&agrave;n về kh&aacute;ch sạn.</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;NEW YORK &ndash; PHILADELPHIA &ndash; WASHINGTON D.C (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn s&aacute;ng tại kh&aacute;ch sạn v&agrave; trả ph&ograve;ng. Xe đưa đo&agrave;n khởi h&agrave;nh đi Philadelphia (khoảng 02h xe di chuyển). Đ&ecirc;́n Philadelphia, đoàn dừng ch&acirc;n tham quan chụp ảnh:</p>\r\n\r\n<ul>\r\n	<li><strong>Xưởng đúc ti&ecirc;̀n xu của Mỹ</strong>&nbsp;(n&ecirc;́u điều kiện cho phép sẽ được v&agrave;o trong tham quan).</li>\r\n	<li>Tham quan&nbsp;<strong>Chu&ocirc;ng tự do &ndash; Libery Bell</strong>: Đ&acirc;y là quả chu&ocirc;ng vang l&ecirc;n ti&ecirc;́ng chu&ocirc;ng khai sinh nước Mỹ được lưu giữ trong m&ocirc;̣t bảo tàng ri&ecirc;ng tại Philadelphia mỗi năm c&oacute; hơn 10 triệu lượt kh&aacute;ch viếng thăm trong đ&oacute; c&oacute; cả Nguy&ecirc;n thủ Quốc gia c&aacute;c nước.</li>\r\n	<li>Tham quan b&ecirc;n ngo&agrave;i&nbsp;<strong>Dinh Đ&ocirc;̣c L&acirc;̣p - Independence Hall</strong>: Hi&ecirc;̣n nay tòa nhà được bảo t&ocirc;̀n nguy&ecirc;n vẹn, trước đ&acirc;y nơi này di&ecirc;̃n ra cu&ocirc;̣c họp th&ocirc;́ng nh&acirc;́t 13 bang của nước Mỹ và là nơi di&ecirc;̃n ra r&acirc;́t đi&ecirc;̀u sự ki&ecirc;̣n lịch sử quan trọng của bu&ocirc;̉i đ&acirc;̀u khai sinh Hoa Kỳ. Nơi đ&acirc;y cũng ch&iacute;nh l&agrave; trụ sở của Lưỡng viện Quốc hội Mỹ thời kỳ non trẻ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng. Sau bữa trưa, xe đưa đo&agrave;n tiếp tục đi Washington D.C. Đến Washington D.C, xe đưa đo&agrave;n đến&nbsp;<strong>Trung t&acirc;m Saigon Eden&nbsp;</strong>&ndash; Trung t&acirc;m kinh doanh, bu&ocirc;n b&aacute;n v&agrave; giao lưu văn ho&aacute; của cộng đồng Kiều b&agrave;o Việt tại thủ đ&ocirc;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng địa phương v&agrave; về kh&aacute;ch sạn nhận ph&ograve;ng, nghỉ ngơi.</p>\r\n\r\n<p>NG&Agrave;Y 4 |&nbsp;WASHINGTON D.C &ndash; LAS VEGAS (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau bữa s&aacute;ng, đoàn trả ph&ograve;ng v&agrave; khởi h&agrave;nh tham quan Washington D.C:</p>\r\n\r\n<ul>\r\n	<li>Tham quan&nbsp;<strong>Nh&agrave; Trắng</strong>&nbsp;(chụp ảnh b&ecirc;n ngo&agrave;i) &ndash; Nơi l&agrave;m việc của đương kim tổng thống Hoa Kỳ, nơi đón ti&ecirc;́p các nguy&ecirc;n thủ qu&ocirc;́c gia.</li>\r\n	<li><strong>Điện Capitol&nbsp;</strong>- T&ograve;a nh&agrave; lưỡng viện Hoa Kỳ, m&ocirc;̣t thi&ecirc;́t k&ecirc;́ ki&ecirc;́n trúc đ&ocirc;̣c đáo. Nơi đ&acirc;y là đ&ocirc;́i trọng quy&ecirc;̀n lực với Nhà Trắng, cùng với Tòa án t&ocirc;́i cao tạo thành th&ecirc;́ ch&acirc;n vạc quy&ecirc;̀n lực trong th&ecirc;̉ ch&ecirc;́ chính trị &ldquo;Tam quy&ecirc;̀n ph&acirc;n l&acirc;̣p&rdquo; của Li&ecirc;n Bang Mỹ.</li>\r\n	<li><strong>Nh&agrave; tưởng niệm Lincoln&nbsp;</strong>- Tổng thống t&agrave;i năng nhất nước Mỹ, người đã có c&ocirc;ng x&oacute;a bỏ chế độ n&ocirc; lệ v&agrave; th&ocirc;́ng nh&acirc;́t 36 bang của Hoa Kỳ v&agrave; vườn tưởng niệm chiến sĩ trận vong của Hoa Kỳ trong 2 cuộc chiến tại Việt Nam v&agrave; Nam Bắc Triều Ti&ecirc;n.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng địa phương. Xe đưa ra s&acirc;n bay đ&aacute;p chuyến bay tối sang Las Vegas (khoảng hơn 5h bay).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n đến Las Vegas, xe đ&oacute;n v&agrave; đưa đo&agrave;n về ăn tối nhẹ v&agrave; về kh&aacute;ch sạn nhận ph&ograve;ng, nghỉ ngơi, tự do trải nghiệm Las Vegas về đ&ecirc;m.&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 5 |&nbsp;LAS VEGAS &ndash; HOOVER DAM &ndash; GRAND CANYON (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xe đưa đo&agrave;n đi ăn tại nh&agrave; h&agrave;ng Việt Nam. Sau bữa s&aacute;ng, đo&agrave;n khởi h&agrave;nh đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Đập thủy điện Hoover l</strong>ớn nhất miền t&acirc;y nước Mỹ. Đ&acirc;y l&agrave; đập thủy điện cung cấp điện cho cả bang Neveda v&agrave; dẫn nước tưới ti&ecirc;u cho 7 bang miền T&acirc;y Hoa Kỳ.</li>\r\n	<li>Hồ nh&acirc;n tạo lớn nhất thế giới&nbsp;<strong>Lake Mead</strong>.</li>\r\n	<li>Điểm thăm quan đặc biệt, duy nhất chỉ c&oacute; tại Du Lịch Việt -&nbsp;<strong>Đại vực Grand Canyon - hẻm n&uacute;i Grand Canyon</strong>&nbsp;vĩ đại &aacute;n ngữ ở tiểu bang Arizona, Mỹ được xếp v&agrave;o h&agrave;ng đệ nhất kỳ quan thi&ecirc;n nhi&ecirc;n của nh&acirc;n loại. N&oacute; nằm trong khu vườn quốc gia khổng lồ Grand Canyon, l&agrave; khu bảo tồn thi&ecirc;n nhi&ecirc;n.</li>\r\n	<li>Tặng v&eacute; Qu&yacute; kh&aacute;ch tham quan v&agrave; chụp ảnh tr&ecirc;n c&acirc;y cầu k&iacute;nh nổi tiếng<strong>&nbsp;Skywalk</strong>&nbsp;(kh&ocirc;ng ho&agrave;n tiền nếu kh&ocirc;ng sử dụng).</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng b&ecirc;n trong Đại vực v&agrave; tiếp tục tham quan trải nghiệm. Xe đưa đo&agrave;n trở về Las Vegas tiếp tục chương tr&igrave;nh mua sắm thực phẩm chức năng, vitamin&hellip;(nếu thời gian di chuyển từ Đại vực về sớm).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng địa phương v&agrave; trở vế kh&aacute;ch sạn nghỉ ngơi, tự do vui chơi, t&igrave;m kiếm vận may tại c&aacute;c casino hoặc tham gia v&agrave;o c&aacute;c chương tr&igrave;nh biểu diễn nghệ thuật đặc sắc chỉ c&oacute; ở Las Vegas.</p>\r\n\r\n<p>NG&Agrave;Y 6 |&nbsp;LAS VEGAS &ndash; SHOPPING OUTLET &ndash; LOS ANGELES - LITTLE SAIGON (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n trả ph&ograve;ng kh&aacute;ch sạn, xe đưa đo&agrave;n đi ăn s&aacute;ng. &nbsp;Đo&agrave;n khởi h&agrave;nh về Los Angeles bằng xe (khoảng 5h xe), Đo&agrave;n dừng ch&acirc;n mua sắm tại c&aacute;c Outlet &ndash; điểm mua sắm hấp dẫn nhất h&agrave;nh tr&igrave;nh. Qu&yacute; kh&aacute;ch c&oacute; cơ hội sở hữu c&aacute;c m&oacute;n hang hiệu đẳng cấp với gi&aacute; rẻ bất ngờ, nhiều ch&iacute;nh s&aacute;ch khuyến mại, sale off tới 70%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng v&agrave; khởi h&agrave;nh về Quận cam thuộc th&agrave;nh phố Los Angeles.&nbsp;<strong>Trung t&acirc;m thương mại Phước Lộc Thọ</strong>&nbsp;- đ&acirc;y được coi l&agrave; trung t&acirc;m văn ho&aacute; v&agrave; kinh doanh bu&ocirc;n b&aacute;n của kiều b&agrave;o Việt tại miền t&acirc;y nước Mỹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xe đưa đo&agrave;n d&ugrave;ng cơm tối, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi.</p>\r\n\r\n<p>NG&Agrave;Y 7 |&nbsp;LOS ANGELES &ndash; SAN DIEGO &ndash; WALMART SHOPPING (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n khởi h&agrave;nh đi tham quan th&agrave;nh phố San Diego &ndash; th&agrave;nh phố lớn thứ 3 v&agrave; cũng l&agrave; th&agrave;nh phố du lịch biển nổi tiếng (khoảng 2h xe &ocirc; t&ocirc;). Tại đ&acirc;y, qu&yacute; kh&aacute;ch sẽ tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>San Diego downtown</strong>&nbsp;&ndash; phố cổ San Diego, nơi ghi dấu ấn của văn ho&aacute; v&agrave; c&ocirc;ng tr&igrave;nh kiến tr&uacute;c của T&acirc;y Ban Nha trong những năm đ&ocirc; hộ v&agrave; đồng ho&aacute; văn ho&aacute; Mexico. Đo&agrave;n tham quan phố mới hiện đại v&agrave; bến cảng vui nhộn.</li>\r\n	<li><strong>Bảo t&agrave;ng chiến hạm San Diego</strong>&nbsp;&ndash; nơi đ&acirc;y từng l&agrave; vị tr&iacute; đ&oacute;ng qu&acirc;n chiến lược của hạm đội hải quan Hoa Kỳ, qu&yacute; kh&aacute;ch c&oacute; cơ hội được ngắm nh&igrave;n c&aacute;c hạm đội cũng như những t&agrave;u chiến c&ograve;n được lưu trữ v&agrave;o bảo quản tr&ecirc;n vịnh.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng địa phương, buổi chiều đo&agrave;n tiếp tục chương tr&igrave;nh tham quan th&agrave;nh phố hoặc xe đưa đo&agrave;n đến bi&ecirc;n giới giữa Mỹ v&agrave; Mexico để mua sắm h&agrave;ng ho&aacute; miễn thuế, xe đưa đo&agrave;n quay trở lại Los Angeles (tuỳ thuộc điều kiện giao th&ocirc;ng v&agrave; quy định bi&ecirc;n giới Mỹ v&agrave; Mexico).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng địa phương. Sau bữa tối, xe đưa đo&agrave;n về kh&aacute;ch sạn nghỉ ngơi, sắp xếp dọn dẹp đồ đạc, h&agrave;nh l&iacute;&hellip;</p>\r\n\r\n<p>NG&Agrave;Y 8 |&nbsp;HOLLYWOOD &ndash; SANTA MONICA &ndash; S&Acirc;N BAY (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa đo&agrave;n khởi h&agrave;nh đi si&ecirc;u thị Walmart để mua sắm đồ ti&ecirc;u d&ugrave;ng, tr&aacute;i c&acirc;y, b&aacute;nh kẹo, thực phẩm l&agrave;m qu&agrave; cho người th&acirc;n. Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng địa phương v&agrave; khởi h&agrave;nh tham quan tham quan Hollywood:</p>\r\n\r\n<ul>\r\n	<li><strong>Biển Santa Monica</strong>&nbsp;- nằm ở ph&iacute;a T&acirc;y của Los Angeles, b&atilde;i biển Santa Monica l&agrave; một biểu tượng của Th&agrave;nh Phố v&agrave; l&agrave; một trong những b&atilde;i biển &nbsp;nổi tiếng v&ugrave;ng Nam Califonia.</li>\r\n	<li><strong>Đại Lộ Danh Vọng (Hollywood Walk of Fame)&nbsp;</strong>&ndash; nơi lưu danh những ng&ocirc;i sao n&ocirc;̉i tiếng c&oacute; đ&oacute;ng g&oacute;p lớn trong l&agrave;ng điện ảnh giải tr&iacute;.</li>\r\n	<li><strong>Nhà hát Dolby&nbsp;</strong>&ndash; nơi di&ecirc;̃n ra l&ecirc;̃ trao giải Oscar danh giá hàng năm của làng đi&ecirc;̣n ảnh Mỹ.</li>\r\n	<li><strong>Nhà hát Trung Hoa</strong>&nbsp;&ndash; nơi c&ocirc;ng chi&ecirc;́u giới thi&ecirc;̣u những b&ocirc;̣ phim Hollywood trước khi tung ra thị trường.</li>\r\n</ul>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xe đ&oacute;n đo&agrave;n ăn tối tại nh&agrave; h&agrave;ng địa phương. Sau bữa tối, xe đưa đo&agrave;n ra s&acirc;n bay đ&aacute;p chuyến bay đ&ecirc;m về Việt Nam. Đo&agrave;n đ&aacute;p chuyến bay về Việt Nam (thời gian bay l&agrave; hơn 11h bay li&ecirc;n tục).<br />\r\nQu&yacute; kh&aacute;ch h&agrave;ng c&oacute; nhu cầu ở lại thăm th&acirc;n hoặc đi tiếp sẽ t&aacute;ch đo&agrave;n trước khi xe đưa ra s&acirc;n bay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 9 |&nbsp;LOS ANGELES - TRANSIT TAIPEI (Ăn uống v&agrave; nghỉ ngơi tr&ecirc;n m&aacute;y bay) (Xem th&ecirc;m)</p>\r\n\r\n<p>M&aacute;y bay cất c&aacute;nh. Ăn uống, nghỉ ngơi tr&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p>NG&Agrave;Y 10 |&nbsp;TAIPEI - H&Agrave; NỘI - TP.HCM (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đến s&acirc;n bay Đ&agrave;i Bắc, đo&agrave;n l&agrave;m thủ tục nối chuyến v&agrave; mua sắm h&agrave;ng miễn thuế tại s&acirc;n bay Đ&agrave;o Vi&ecirc;n &ndash; một trong những s&acirc;n bay được đ&aacute;nh gi&aacute; l&agrave; c&oacute; hoạt động v&agrave; c&aacute;c mặt h&agrave;ng miễn thuế đa dạng v&agrave; phong ph&uacute; tốp 10 tr&ecirc;n thế giới. M&aacute;y bay cất c&aacute;nh về Việt Nam. Về tới s&acirc;n bay Việt Nam, qu&yacute; kh&aacute;ch l&agrave;m thủ tục nhập cảnh v&agrave; nhận lại h&agrave;nh l&yacute;.</p>\r\n', '2021-11-24', ' 10 ngày 9 đêm', ' Từ Hồ Chí Minh  ', 14, 65500000, 1, 2, 0),
(8, 'Du lịch Mỹ mùa Xuân - Los Angeles - Las Vegas - San Diego từ Sài Gòn giá tốt', 'Du lịch Mỹ mùa Xuân hành trình đến với Bờ Tây Hoa Kỳ, nơi tọa lạc của những thành phố giải trí phồn hoa bậc nhất thế giới. Thăm kinh đô điện ảnh Hollywood ở Los Angeles, nơi đi đầu trong lĩnh vực sản xuất phim ảnh và biên tập kỹ xảo ấn tượng. Đặt chân đến Đại Lộ Danh Vọng nơi vinh danh những tên tuổi nổi tiếng trong hàng thập kỷ qua. Trong tour du lịch Mỹ mùa Xuân này, du khách sẽ có những ngày vui chơi quên lối về ở thành phố không ngủ về đêm Las Vegas. Nơi hội tụ của những sòng bạc đắt đỏ, những khu phức hợp giải trí đẳng cấp thế giới. Mùa Xuân là mùa đẹp nhất trong năm, Du Lịch Việt sẽ đưa bạn đến với thành phố San Diego, ngắm trọn vẻ đẹp của thành phố biển xinh đẹp nhất nước Mỹ. Đi tour Mỹ mùa xuân 2020 khám phá Los Angeles, Las Vegas và San Diego sẽ là lựa chọn tuyệt vời dành cho du khách', '1637745645_dai-lo-las-vegas.jpg', '1637745645_vuon-hoa-khach-san-bellagio.jpg', '1637745645_tour-du-lich-my-mua-xuan.jpg', '1637745645_nha-hat-kodak.jpg', '<p>NG&Agrave;Y 1 |&nbsp;TP.HCM - TRANSIT TAIPEI - LOS ANGELES &ndash; LAS VEGAS (tr&ecirc;n m&aacute;y bay, ăn tối)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay Qu&ocirc;́c t&ecirc;́ T&acirc;n Sơn Nhất. Hướng dẫn vi&ecirc;n đ&oacute;n v&agrave; hướng dẫn Qu&yacute; kh&aacute;ch l&agrave;m thủ tục check-in. Đo&agrave;n đ&aacute;p chuyến bay đi Taipei l&uacute;c 11h00 s&aacute;ng. Đến Taipei, Đo&agrave;n tiếp tục nối chuyến bay đi Los Angeles.&nbsp;</li>\r\n	<li>Đo&agrave;n đến Los Angeles c&ugrave;ng ng&agrave;y l&uacute;c ~ 12h25. Xe đưa qu&yacute; kh&aacute;ch khởi h&agrave;nh đi Las Vegas. Đến Las Vegas, đo&agrave;n d&ugrave;ng cơm chiều. Nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;LAS VEGAS &ndash; HOOVER DAM &ndash; LAKE MEAD (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu qu&yacute; kh&aacute;ch muốn tham quan c&aacute;c tour tự chọn đi Grand Canyon, xin vui l&ograve;ng b&aacute;o trước với HDV 1 ng&agrave;y để đăng k&yacute; mua v&eacute; ri&ecirc;ng.</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Sau bữa s&aacute;ng, đo&agrave;n khởi h&agrave;nh đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Đập thủy điện Hoover</strong>&nbsp;lớn nhất nước Mỹ v&agrave; hồ nh&acirc;n tạo lớn nhất thế giới Lake Mead. Đ&acirc;y l&agrave; đập thủy điện cung cấp điện cho cả bang Neveda v&agrave; dẫn nước tưới ti&ecirc;u cho 7 bang miền T&acirc;y Hoa Kỳ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n trở về Las Vegas ăn trưa:</p>\r\n\r\n<ul>\r\n	<li>Xe đưa đo&agrave;n đến khu mua sắm<strong>&nbsp;Las Vegas Premium Factory Outlet</strong>, đo&agrave;n thỏa th&iacute;ch mua sắm thời trang h&agrave;ng hiệu với gi&aacute; cực rẻ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng. Xe đưa đo&agrave;n về lại kh&aacute;ch sạn nghỉ ngơi v&agrave; tự do sinh hoạt. Hoặc qu&yacute; kh&aacute;ch c&oacute; thể đăng k&yacute; chương tr&igrave;nh tham quan Las Vegas By Night đắc sắc (chi ph&iacute; tự t&uacute;c):</p>\r\n\r\n<ul>\r\n	<li>Tham quan vườn hoa trong kh&aacute;ch sạn Bellagio. Thưởng thức chương tr&igrave;nh nhạc nước đặc sắc ph&iacute;a trước kh&aacute;ch sạn Bellagio.</li>\r\n	<li>Tham quan kh&aacute;ch sạn Casino Venetian, chi&ecirc;m ngưỡng những d&ograve;ng k&ecirc;nh b&ecirc;n trong m&ocirc; phỏng th&agrave;nh phố nổi Venice.</li>\r\n	<li>Xe đưa đo&agrave;n dạo 1 v&ograve;ng Đại Lộ Las Vegas, ngắm nh&igrave;n &aacute;nh đ&egrave;n rực s&aacute;ng c&oacute; thể nh&igrave;n thấy từ c&aacute;c xa trung t&acirc;m 40km tr&ecirc;n đỉnh kh&aacute;ch sạn Luxur, ngắm c&aacute;c c&ocirc;ng tr&igrave;nh kh&aacute;ch sạn Casino ho&agrave;nh tr&aacute;ng, hiện đại v&agrave; lớn nhất tr&ecirc;n thế giới: New York &ndash; New York, MGM, Tropicana, Paris Paris, Mote Carlo, Treasure Island&hellip;</li>\r\n	<li>Hoặc Qu&yacute; kh&aacute;ch c&oacute; thể tự do thưởng th&uacute;c c&aacute;c live show đặc sắc về đ&ecirc;m: &quot;Le R&ecirc;ve&quot; tại Wynn với h&agrave;ng trăm nữ vũ c&ocirc;ng cũng l&agrave; VĐV bơi lặn chuy&ecirc;n nghiệp xuất hiện ngược tr&ecirc;n mặt nước (v&eacute; tự t&uacute;c tr&ecirc;n 175$ v&agrave; phải đăng k&yacute; trước), hoặc Show Jubille, Show &quot;O&quot; tại Bellagio (v&eacute; tự t&uacute;c, gi&aacute; v&eacute; t&ugrave;y thời điểm),&hellip; hay qu&yacute; kh&aacute;ch c&oacute; thể tự do kh&aacute;m ph&aacute; Las Vegas về đ&ecirc;m.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;LAS VEGAS - UNIVERSAL STUDIO - LOS ANGELES (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đoàn trả ph&ograve;ng Kh&aacute;ch sạn, xe đưa đo&agrave;n đến tham quan v&agrave; chụp ảnh lưu niệm tại khu vực biển ch&agrave;o Las Vegas &ndash; biểu tượng của th&agrave;nh phố. Khởi h&agrave;nh về lại Hollywood. Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau bữa trưa, đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Phim trường Universal (Chi ph&iacute; tự t&uacute;c)</strong>: Xem Nh&agrave; ma, Phim kh&ocirc;ng gian 4 chiều Shrek, Đi xe Tram xem toàn cảnh phim trường, nơi ra đời c&aacute;c bộ phim nổi tiếng: Kingkong&hellip;thưởng thức tr&iacute;ch đoạn &ldquo;Under Water World&rdquo;, Qu&yacute; kh&aacute;ch tự do tham gia c&aacute;c tr&ograve; chơi cảm gi&aacute;c: &ldquo;Mummy Revenge, Vượt Th&aacute;c,&hellip;&rdquo;. Qu&yacute; kh&aacute;ch sẽ được tận mắt chứng kiến c&aacute;c kỹ xảo &acirc;m thanh kh&oacute;i lửa v&agrave; nước trong c&aacute;c cảnh động đất, ch&aacute;y nh&agrave;, nổ xe, mưa v&agrave; nước lũ. Xem phim kh&ocirc;ng gian 4 chiều, thử cảm gi&aacute;c hồi hộp trong hầm mộ của bộ phim Mummy Return, chụp h&igrave;nh c&ugrave;ng c&aacute;c nh&acirc;n vật của c&aacute;c bộ phim nổi tiếng như Người nhện, Người dơi, Thuyền trưởng Jack Sparrow v.v... xem phim 3D Kingkong - Bộ phim 3D lớn nhất thế giới về mức độ ho&agrave;nh tr&aacute;ng - Qu&yacute; kh&aacute;ch như lạc v&agrave;o khu rừng nguy&ecirc;n sinh tận mắt chứng kiến những pha h&agrave;nh động.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp tục về lại Los Angeles. Ăn tối tại nh&agrave; h&agrave;ng. Đo&agrave;n về kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 4 |&nbsp;LOS ANGELES &ndash; LITTLE SAIGON &ndash; SAN DIEGO - SHOPPING (Ăn s&aacute;ng, tối) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Xe đưa đo&agrave;n tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Đại Lộ Danh Vọng (Hollywood Walk of Fame)</strong>&nbsp;&ndash; nơi lưu danh những ng&ocirc;i sao n&ocirc;̉i tiếng c&oacute; đ&oacute;ng g&oacute;p lớn trong l&agrave;ng điện ảnh, truyền h&igrave;nh, &acirc;m nhạc của Hoa Kỳ.</li>\r\n	<li><strong>Nhà hát Kodak</strong>&nbsp;&ndash; nơi di&ecirc;̃n ra l&ecirc;̃ trao giải Oascar danh giá hành năm của làng đi&ecirc;̣n ảnh Mỹ.</li>\r\n	<li><strong>Nhà hát Trung Hoa</strong>&nbsp;&ndash; nơi c&ocirc;ng chi&ecirc;́u giới thi&ecirc;̣u những b&ocirc;̣ phim Hollywood trước khi tung ra thị trường.</li>\r\n	<li><strong>Khu Ph&uacute;c Lộc Thọ, Little Saigon</strong>&nbsp;- nơi cộng đồng người Việt tập trung sinh sống đ&ocirc;ng nhất tại miền Nam bang California. Qu&yacute; kh&aacute;ch c&oacute; cơ hội tham quan c&aacute;c trung t&acirc;m ca nhạc hải ngoại nổi tiếng như Th&uacute;y Nga &ndash; Paris By night, V&acirc;n Sơn,&hellip;&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn 1 trong 2 t&ugrave;y chọn chương tr&igrave;nh:</strong><br />\r\n<strong><em>Option 1:</em></strong>&nbsp;tự do mua sắm tại c&aacute;c si&ecirc;u thị lớn như: Costo, Best Buy, Fries Electronic, Wall Mart, Macy, Penny&hellip; Ăn trưa tự t&uacute;c trong si&ecirc;u thị. Tự do mua sắm đến giờ tập trung xe đưa đo&agrave;n đi ăn tối.&nbsp;</p>\r\n\r\n<p><em><strong>Option 2:</strong></em>&nbsp;đăng k&yacute; chương tr&igrave;nh tham quan San Diego &ndash; th&agrave;nh phố biển tiếp gi&aacute;p với bi&ecirc;n giới Mexico v&agrave; l&agrave; th&agrave;nh phố c&oacute; đ&ocirc;ng người Việt sinh sống (chi ph&iacute; tự t&uacute;c): Bến cảng San Diego nơi neo đậu h&agrave;ng kh&ocirc;ng mẫu hạm USS Midway từng tham chiến trong chiến tranh Thế giới thứ 2, chiến tranh Việt Nam. Chụp ảnh với bức tượng Nụ h&ocirc;n thủy thủ nổi tiếng do nhiếp ảnh gia người Mỹ Lawrence Verria chụp tại Quảng trường thời đại sau khi kết th&uacute;c chiến tranh thế giới thứ 2.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tự t&uacute;c v&agrave; tiếp tục h&agrave;nh tr&igrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Phố cổ San Diego</strong>&nbsp;&ndash; nơi khai sinh ra tiểu bang Califfornia, mang đậm n&eacute;t kiến tr&uacute;c đặc trưng của Mexico.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Về lại Los Angeles. Xe đưa đo&agrave;n d&ugrave;ng bữa tối. Về kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>NG&Agrave;Y 5 |&nbsp;LOS ANGELES &ndash; SHOPPING - S&Acirc;N BAY - TRANSIT TAIPEI (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đoàn tự do mua sắm tại c&aacute;c si&ecirc;u thị lớn như: Costo, Best Buy, Fries Electronic, Wall Mart, Macy, Penny&hellip; Xe đưa đo&agrave;n d&ugrave;ng bữa trưa, đo&agrave;n tiếp tục tự do mua sắm đến giờ tập trung xe đưa đo&agrave;n đi ăn tối v&agrave; khởi h&agrave;nh ra s&acirc;n bay đ&aacute;p chuyến bay về Việt Nam. Qu&yacute; kh&aacute;ch ăn uống v&agrave; nghỉ ngơi tr&ecirc;n m&aacute;y bay.<br />\r\n(Qu&yacute; kh&aacute;ch c&oacute; thể ở lại thăm th&acirc;n nh&acirc;n theo thời hạn visa cho ph&eacute;p)<br />\r\n&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 6 |&nbsp;TAIPEI - TP.HCM (tr&ecirc;n m&aacute;y bay) (Xem th&ecirc;m)</p>\r\n\r\n<p>Đến Taipei, qu&yacute; kh&aacute;ch tiếp tục nối chuyến bay về TP.HCM.</p>\r\n\r\n<p>NG&Agrave;Y 7 |&nbsp;TP.HCM (Xem th&ecirc;m)</p>\r\n\r\n<p>Đến S&acirc;n bay T&acirc;n Sơn Nh&acirc;́t l&uacute;c 10:00 s&aacute;ng. Qu&yacute; kh&aacute;ch l&agrave;m thủ tục nhập cảnh.<br />\r\nKết th&uacute;c chương tr&igrave;nh. Chia tay v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch.&nbsp;</p>\r\n', '2021-11-24', ' 7 ngày 6 đêm', 'Từ Hồ Chí Minh  ', 9, 33990000, 1, 2, 0);
INSERT INTO `tour` (`id_tour`, `ten`, `tomTat`, `hinh1`, `hinh2`, `hinh3`, `hinh4`, `lichTrinh`, `ngay`, `thoiGian`, `xuatPhat`, `soLuongNguoi`, `gia`, `trangThai`, `id_theLoai`, `id_taiKhoan`) VALUES
(10, 'Du lịch Tết Nguyên đán 2020 - Dubai - Abu Dhabi từ Sài Gòn giá tốt', 'Du lịch Dubai - Du lịch Dubai Tết Nguyên Đán 2020 là một trong những lựa chọn tuyệt vời để bạn tận hưởng những ngày nghỉ bên gia đình, bạn bè. Quốc gia này không chỉ có cảnh đẹp mê mẩn lòng người mà còn lưu giữ nền văn hóa đặc sắc cùng nền ẩm thực đa dạng phong phú hứa hẹn hạ gục trái tim du khách ngay từ ánh mắt đầu tiên. Hãy cùng Du Lịch Việt khám phá tour du lịch Tết âm lịch Dubai có gì hấp dẫn nhé.', '1637746116_du-lich-dubai-tet-nguyen-dan-gia-tot-du-lich-viet.jpg', '1637746116_du-lich-dubai-tet-nguyen-dan-du-lich-viet.jpg', '1637746116_du-lich-dubai-tet-am-lich-2020-du-lich-viet.jpg', '1637746116_du-lich-dubai-tet-2020-du-lich-viet.jpg', '<p>NG&Agrave;Y 1 |&nbsp;TP.HCM &ndash;DUBAI ( Ăn nhẹ tr&ecirc;n m&aacute;y bay)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>21:00 Q&uacute;y kh&aacute;ch tập trung tại ga đi quốc tế tại s&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n c&ocirc;ng ty Du Lịch Việt sẽ đ&oacute;n đo&agrave;n v&agrave; l&agrave;m thủ tục đ&aacute;p chuyến bay&nbsp;<strong>EK393 (23:55- &ndash; 04:25+1)</strong>&nbsp;đi Dubai. Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;DUBAI ( Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đến Dubai, xe v&agrave; hướng dẫn vi&ecirc;n địa phương đ&oacute;n đo&agrave;n đi ăn s&aacute;ng, sau đ&oacute; tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Bảo t&agrave;ng Dubai</strong>&nbsp;- Nơi trưng b&agrave;y t&aacute;i hiện sinh động cuộc sống thường ng&agrave;y, đặc biệt l&agrave; những bức tượng với m&agrave;u sắc, k&iacute;ch cỡ v&agrave; trang phục như người thật, khiến du kh&aacute;ch &nbsp;ngỡ m&igrave;nh đang bước v&agrave;o thế giới của người Ả&nbsp; Rập xưa.</li>\r\n	<li><strong>Abra Ride (taxi nước)</strong>&nbsp;&ndash; thuyền sử dụng hệ thống động cơ dưới nhỏ được gọi l&agrave; Abras. Thuyền c&oacute; thể chứa khoảng 12 người được đưa đi du ngoạn từ Deira đến Bui Dubai.</li>\r\n	<li><strong>Gold &amp; Spice Souk</strong>&nbsp;&ndash; Tự do mua sắm tại chợ Gold Souk (chợ v&agrave;ng) &ndash; với&nbsp; hơn 200 tiệm v&agrave;ng trưng b&agrave;y c&aacute;c loại trang sức từ xưa đến nay, từ loại v&agrave;ng 18,21,22,24 cara, Spice Souk (chợ gia vị) &ndash; với rất nhiều gia vị từ trầm hương đến c&aacute;c loại thảo mộc.</li>\r\n	<li>Qu&yacute; kh&aacute;ch tham quan v&agrave; mua sắm tại cửa h&agrave;ng đồ da</li>\r\n</ul>\r\n\r\n<p>Đo&agrave;n về kh&aacute;ch sạn nghỉ ngơi.<br />\r\nBuổi chiều đo&agrave;n&nbsp;<strong>tham quan vườn hoa Miracle Garden</strong>&nbsp;&ndash; một điểm kh&ocirc;ng thể bỏ qua khi đến Dubai. Đ&acirc;y l&agrave; 1 vườn hoa tự nhi&ecirc;n nằm ngay giữa l&ograve;ng sa mạc c&oacute; qui m&ocirc; lớn nhất thế giới. Đến khu vườn qu&yacute; Kh&aacute;ch kh&ocirc;ng những m&atilde;n nh&atilde;n với sắc hoa m&agrave; c&ograve;n ấn tượng với c&aacute;ch thiết kế mang t&iacute;nh tưởng tượng cao.<br />\r\nĂn tối tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Dubai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;DUBAI - SA MẠC SAFARI ( Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Palm Island &ndash; đo&agrave;n tham quan Palm Jumeirad - Đảo Cọ nh&acirc;n tạo</strong>&nbsp;lớn nhất tr&ecirc;n thế giới bằng hệ thống xe điện tr&ecirc;n kh&ocirc;ng,được xem l&agrave; kỳ quan thứ 8 của thế giới. Chi&ecirc;m ngưỡng kh&aacute;ch sạn Atlantis với 1.529 ph&ograve;ng được trang tr&iacute; lộng lẫy, xa hoa. G&iacute;a ph&ograve;ng l&ecirc;n đến h&agrave;ng chục ngh&igrave;n USD mỗi đ&ecirc;m.</li>\r\n	<li><strong>Chụp h&igrave;nh b&ecirc;n ngo&agrave;i kh&aacute;ch sạn Burj Al Arab</strong>&nbsp;&ndash; Kh&aacute;ch sạn 7 sao đầu ti&ecirc;n&nbsp; tr&ecirc;n thế giới.</li>\r\n	<li><strong>&nbsp;Dubai Mall, tham quan th&aacute;p Burj Khalifa (bao gồm v&eacute; l&ecirc;n th&aacute;p)</strong>&nbsp;&ndash; to&agrave; nh&agrave; ch&iacute;nh thứckh&aacute;nh th&agrave;nh v&agrave;o ng&agrave;y 04/01/2010 tại khu trung t&acirc;m t&agrave;i ch&iacute;nh của Dubai, với những kỷlục như: t&ograve;a nh&agrave; cao nhất thế giới với tổng cộng l&agrave; 164 tầng, 828m. Tự do mua sắm tại trung t&acirc;m thương mại Dubai Mall.</li>\r\n	<li>Qu&yacute; kh&aacute;ch tham quan v&agrave; mua sắm tại cửa h&agrave;ng b&aacute;n thảm</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng, đo&agrave;n trở về kh&aacute;ch sạn nghỉ ngơi.<br />\r\nBuổi chiều, đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Sa mạc Safari bằng xe đặc chủng Land Cruiser</strong>, qu&yacute; kh&aacute;ch sẽ được trải nghiệm cảm gi&aacute;c đi xe trượt tr&ecirc;n c&aacute;t hay cưỡi lạc đ&agrave; tr&ecirc;n sa mạc.H&uacute;t Shisa, vẽ tay Hanna - một lối vẽ trang tr&iacute; bằng bột m&agrave;u l&ecirc;n tay hoặc ch&acirc;n rất phổ biến ở Trung Đ&ocirc;ng.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Buổi tối qu&yacute; kh&aacute;ch thưởng thức tiệc nướng BBQ v&agrave; xem biểu diễn m&uacute;a bụng truyền thống tr&ecirc;n nền nhạc Ả Rập, thử trang phục truyền thống của người Ả Rập&hellip;Về kh&aacute;ch sạn tại Dubai nghỉ đ&ecirc;m.</p>\r\n\r\n<p>NG&Agrave;Y 4 |&nbsp;DUBAI &ndash; ABU DHABI (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn, đo&agrave;n khởi h&agrave;nh đi Abu Dhabi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Th&aacute;nh đường Hồi gi&aacute;o Sheikh Zayed hay Grand Mosque</strong>&nbsp;&ndash; th&aacute;nh đường lớn nhất quốc gia c&oacute; kiến tr&uacute;c nguy nga lộng lẫy h&agrave;ng đầu thế giới, l&agrave; nơi tưởng niệm Sheikh Zayed bin Sultan Al Nahyan, vị tổng thống đầu ti&ecirc;n v&agrave; cũng l&agrave; niềm tự h&agrave;o của c&aacute;c tiểu vương quốc Ả Rập thống nhất.(Lưu &yacute;: Q&uacute;y kh&aacute;ch tham quan th&aacute;nh đường phải mặc quần &aacute;o k&iacute;nh đ&aacute;o)</li>\r\n	<li>Xe đưa đo&agrave;n dọc theo đại lộ Corniche nổi tiếng thưởng ngoạn cảnh đẹp b&ecirc;n Vịnh Ba Tư.</li>\r\n	<li>Ngắm nh&igrave;n t&ograve;a l&acirc;u đ&agrave;i nguy nga v&agrave; sang trọng Presidential Palace v&agrave; Emirates Palace.</li>\r\n	<li><strong>Chụp h&igrave;nh b&ecirc;n ngo&agrave;i ph&ograve;ng trưng b&agrave;y Ferrari World</strong>&nbsp;&ndash; Đ&acirc;y l&agrave; c&ocirc;ng vi&ecirc;n giải tr&iacute; trong nh&agrave; lớn nhất thế giới với hơn 20 đường t&agrave;u cao tốc tr&ecirc;n kh&ocirc;ng c&oacute; tốc độ nhanh nhất thế giwosi 240km/h. Q&uacute;y kh&aacute;ch c&oacute; thể tham quan c&aacute;c hoạt động hấp dẫn trong c&ocirc;ng vi&ecirc;n như&nbsp; tham gia v&agrave;o c&aacute;c khu vực kỹ thuật, quan s&aacute;t, điều h&agrave;nh v&agrave; kh&aacute;m ph&aacute; những khoảng khắc đua xe ấn tượng của Ferrari (chi ph&iacute; tự t&uacute;c). Chi&ecirc;m ngưỡng đường đua c&ocirc;ng th&uacute;c F1.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ăn trưa buffet tại kh&aacute;ch sạn 5*,</strong>&nbsp;đo&agrave;n tiếp tục tham quan mua sắm c&aacute;c mặt h&agrave;ng n&ocirc;ng sản truyền thống tại Dates Market.<br />\r\nChiều đo&agrave;n khởi h&agrave;nh về Dubai.</p>\r\n\r\n<p><strong>Ăn tối tại nh&agrave; h&agrave;ng xoay độc nhất tại th&agrave;nh phố Dubai</strong>&nbsp;- Al Dawaar &ndash; Revolving Restaurant &amp;Lounge . Ngắm cảnh tuyệt đep của th&agrave;nh phố Dubai về đ&ecirc;m, với t&ograve;a nh&agrave; chọc trời Burj Khalifa ở ph&iacute;a đường ch&acirc;n trời , v&agrave; trải nghiệm ăn uống đẳng cấp sang trọng 5 sao.<br />\r\nNghỉ đ&ecirc;m tại Dubai.</p>\r\n\r\n<p>NG&Agrave;Y 5 |&nbsp;DUBAI &ndash; TP.HỒ CH&Iacute; MINH (Ăn s&aacute;ng) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn, l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh ra s&acirc;n bay v&agrave; l&agrave;m thủ tục đ&aacute;p chuyến bay EK 392 (09:40 - 19:35) về TP. HCM. Kết th&uacute;c chương tr&igrave;nh, hẹn gặp lại qu&yacute; kh&aacute;ch.</p>\r\n', '2021-11-24', '4 ngày 4 đêm', ' Từ Hồ Chí Minh  ', 10, 69999400, 1, 2, 0),
(11, 'Du lịch hè Trương Gia Giới - Phượng Hoàng Cổ Trấn 2020 6 ngày từ Sài Gòn giá tốt', '(Nếu vào mùa đông có tuyết rơi, vì đảm bảo an toàn cho Qúy khách sẽ  không tham quan hành lang kính và không sử dụng xe di chuyển qua 99 khúc cua chỉ lên và xuống bằng cáp treo)  \r\nĐoàn khởi hành đi Phượng Hoàng Cổ Trấn (đoàn đi xe khoảng 4 đến 5 tiếng)\r\nĐến nơi, nhận phòng khách sạn và nghỉ ngơi. Nghỉ đêm tại Phượng Hoàng Cổ Trấn.', '1637746317_du-lich-phuong-hoang-co-tran-gia-re-du-lich-viet.jpg', '1637746317_du-lich-he-truong-gia-gioi-phuong-hoang-co-tran-gia-tot-du-lich-viet.jpg', '1637746317_du-lich-he-phuong-hoang-co-tran-gia-tot-du-lich-viet(1).jpg', '1637746317_du-lich-he-gia-khuyen-mai-du-lich-viet.jpg', '<p>NG&Agrave;Y 1 |&nbsp;TP.HCM &ndash;TRƯƠNG GIA GIỚI (Q&uacute;y kh&aacute;ch được tặng fastfood tại s&acirc;n bay VN)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất, cổng số 2 &ndash; ga đi quốc tế, l&agrave;m thủ tục đ&aacute;p chuyến bay thẳng&nbsp;<strong><em>VJ2552 (15:30 &ndash; 20:30)</em></strong>&nbsp;đi Trương Gia Giới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;TRƯƠNG GIA GIỚI &ndash; THI&Ecirc;N M&Ocirc;N SƠN (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng. L&agrave;m thủ tục trả ph&ograve;ng. Khởi h&agrave;nh đi Thi&ecirc;n M&ocirc;n Sơn.</p>\r\n\r\n<ul>\r\n	<li><strong>Thi&ecirc;n M&ocirc;n Sơn&nbsp;</strong>- nằm trong vườn quốc gia n&uacute;i Thi&ecirc;n M&ocirc;n. Ngọn n&uacute;i n&agrave;y rất nổi tiếng với con đường tr&ecirc;n trời v&agrave; hệ thống c&aacute;p treo tại Thi&ecirc;n M&ocirc;n được tuy&ecirc;n bố trong c&aacute;c ấn phẩm du lịch l&agrave; &quot;c&aacute;p treo d&agrave;i nhất tại một ngọn n&uacute;i cao nhất tr&ecirc;n thế giới&rdquo; với 98 cabin c&aacute;p treo v&agrave; tổng chiều d&agrave;i l&ecirc;n tới hơn 7.400 m&eacute;t, độ cao của ga tr&ecirc;n của tuyến c&aacute;p treo l&agrave; 1.279 m&eacute;t (bao gồm xe c&ocirc;ng cộng v&agrave; 1 lượt c&aacute;p treo đi l&ecirc;n). &nbsp;Khi l&ecirc;n tới đỉnh của Thi&ecirc;n M&ocirc;n Sơn, qu&yacute; kh&aacute;ch sẽ đi theo con đường bao quanh những đỉnh n&uacute;i, xuy&ecirc;n từ đỉnh n&uacute;i n&agrave;y sang đỉnh n&uacute;i kh&aacute;c, một b&ecirc;n l&agrave; n&uacute;i, một b&ecirc;n l&agrave; m&acirc;y</li>\r\n	<li>Tiếp tục, qu&yacute; kh&aacute;ch vượt qua 999 bậc thang thẳng đứng để l&ecirc;n đến&nbsp;<strong>Cổng Trời</strong>&nbsp;- nơi giao thoa giữa trời v&agrave; đất.</li>\r\n	<li>Tham quan h&agrave;nh lang k&iacute;nh độc đ&aacute;o tạo cảm gi&aacute;c như đang đi tr&ecirc;n trời.</li>\r\n</ul>\r\n\r\n<p><strong><em>(Nếu v&agrave;o m&ugrave;a đ&ocirc;ng c&oacute; tuyết rơi, v&igrave; đảm bảo an to&agrave;n cho Q&uacute;y kh&aacute;ch sẽ&nbsp; kh&ocirc;ng tham quan h&agrave;nh lang k&iacute;nh v&agrave; kh&ocirc;ng sử dụng xe di chuyển qua 99 kh&uacute;c cua chỉ l&ecirc;n v&agrave; xuống bằng c&aacute;p treo)&nbsp;&nbsp;</em></strong><br />\r\nĐo&agrave;n khởi h&agrave;nh đi Phượng Ho&agrave;ng Cổ Trấn (đo&agrave;n đi xe khoảng 4 đến 5 tiếng)<br />\r\nĐến nơi, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Phượng Ho&agrave;ng Cổ Trấn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;PHƯỢNG HO&Agrave;NG CỔ TRẤN (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n tham quan Th&agrave;nh cổ&nbsp; Phượng Ho&agrave;ng - nơi c&oacute; gi&aacute; trị&nbsp; lịch sử văn h&oacute;a l&acirc;u đời từng được ca ngợi l&agrave; th&agrave;nh phố cổ đẹp nhất Trung Quốc. Đo&agrave;n tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Lầu Mi&ecirc;u Mi&ecirc;u (chụp h&igrave;nh b&ecirc;n ngo&agrave;i)</strong></li>\r\n	<li><strong>Bắc M&ocirc;n Cổ Th&agrave;nh (chụp h&igrave;nh b&ecirc;n ngo&agrave;i)</strong></li>\r\n	<li><strong>Lầu Phong Thủy Hồng Kiều (chụp h&igrave;nh b&ecirc;n ngo&agrave;i)</strong></li>\r\n</ul>\r\n\r\n<p>Ăn tối, nhận ph&ograve;ng kh&aacute;ch sạn. Nghỉ đ&ecirc;m tại Phương Ho&agrave;ng Cổ Trấn Hoặc buổi tối qu&yacute; kh&aacute;ch c&oacute; thể tự do kh&aacute;m ph&aacute; cảnh đ&ecirc;m Th&agrave;nh cổ Phượng Ho&agrave;ng đẹp lung linh về đ&ecirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 4 |&nbsp;PHƯỢNG HO&Agrave;NG CỔ TRẤN &ndash; PH&Ugrave; DUNG TRẤN &ndash; TRƯƠNG GIA GIỚI (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn, Đo&agrave;n l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>&nbsp;Ph&ugrave; Dung Trấn</strong>&nbsp;- một thị trấn cổ hơn 2.000 tuổi với những c&ocirc;ng t&igrave;nh kiến tr&uacute;c cổ k&iacute;nh, những th&aacute;c nước h&ugrave;ng vĩ,.. tất cả h&ograve;a quện với nhau tạo n&ecirc;n một bức tranh cổ trấn đẹp mơ m&agrave;ng.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng. Sau đ&oacute;, xe khởi h&agrave;nh đi Vũ Lăng Nguy&ecirc;n (đo&agrave;n đi xe khoảng 4 đến 5 tiếng)</p>\r\n\r\n<ul>\r\n	<li>Tham quan v&agrave; mua sắm tại Cửa h&agrave;ng tơ lụa v&agrave; Cửa h&agrave;ng tr&agrave;.</li>\r\n</ul>\r\n\r\n<p>Sau khi ăn tối, Qu&yacute; kh&aacute;ch c&oacute; thể xem show biểu diễn nổi tiếng&nbsp;<strong>Rạng Rỡ Tương T&acirc;y</strong>,&nbsp;với d&agrave;n diễn vi&ecirc;n tr&igrave;nh diễn bằng ca m&uacute;a n&oacute;i về Văn H&oacute;a v&agrave; Phong Tục của D&acirc;n Tộc Mi&ecirc;u, D&acirc;n Tộc Thổ Gia, D&acirc;n Tộc Bạch&hellip;<strong>&nbsp;Gi&aacute; v&eacute; 60USD/người&nbsp;<em>~&nbsp;</em></strong><strong>400RMB/người<em>.( Chi Ph&iacute; Tự T&uacute;c)</em></strong>&nbsp;<br />\r\nNghỉ đ&ecirc;m tại Vũ Lăng Nguy&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 5 |&nbsp;TRƯƠNG GIA GIỚI &ndash; THI&Ecirc;N TỬ SƠN &ndash; VŨ LĂNG NGUY&Ecirc;N (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n khởi h&agrave;nh tham quan&nbsp;<strong>Thi&ecirc;n Tử Sơn:</strong><br />\r\nĐo&agrave;n đi&nbsp;<strong>thang m&aacute;y Bạch Long</strong>&nbsp;ngo&agrave;i trời cao nhất thế giới chiều cao 330 m&eacute;t, đến&nbsp;<strong>khu phong cảnh Vi&ecirc;n Gia Giới</strong>, nơi đo&agrave;n phim Hollywood lấy bối cảnh trong Phim Avatar. Đặc biệt, Q&uacute;y kh&aacute;ch tham gia tour m&ugrave;a l&aacute; đỏ chắc sẽ ko thể bỏ qua điểm đến hấp dẫn n&agrave;y n&agrave;y.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;<strong>Đ&agrave;i M&ecirc; Hồn, Hậu Hoa Vi&ecirc;n, Thi&ecirc;n hạ đệ nhất kiều</strong>, c&acirc;y cầu bằng đ&aacute; bắt ngang 2 quả n&uacute;i v&ocirc; c&ugrave;ng h&ugrave;ng vĩ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng địa phương. Đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li>Tham quan&nbsp;<strong>Kim Ti&ecirc;n Kh&ecirc;:</strong>&nbsp;với&nbsp;<strong>Thủy Nhiễu Tứ M&ocirc;n&nbsp;</strong>- l&agrave; nơi tụ họp của 4 d&ograve;ng chảy&nbsp;<strong>Kim Bi&ecirc;n Kh&ecirc;, Tố Kh&ecirc;, Uy&ecirc;n Ương Kh&ecirc;</strong>&nbsp;v&agrave;<strong>&nbsp;Long Vĩ Kh&ecirc;</strong></li>\r\n	<li>Cửa h&agrave;ng Bạc Ch&acirc;u Sa.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng. Xe đưa đo&agrave;n về kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Vũ Lăng Nguy&ecirc;n.</p>\r\n\r\n<p>NG&Agrave;Y 6 |&nbsp;VŨ LĂNG NGUY&Ecirc;N - TRƯƠNG GIA GIỚI - TP.HỒ CH&Iacute; MINH (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. L&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh tham</p>\r\n\r\n<ul>\r\n	<li><strong>Cầu k&iacute;nh Thi&ecirc;n V&acirc;n Độ</strong>&nbsp;- l&agrave; t&aacute;c phẩm của Haim Dotan - kiến tr&uacute;c sư nổi tiếng người Israel. Với chiều d&agrave;i 430 m, lối đi rộng 1,5 m&nbsp;v&agrave; độ cao 300 m so với mặt đất, c&acirc;y cầu k&iacute;nh Thi&ecirc;n V&acirc;n Độ được mệnh danh l&agrave; c&acirc;y cầu k&iacute;nh d&agrave;i nhất thế giới. Bề mặt cầu được l&agrave;m&nbsp;bằng k&iacute;nh trong suốt d&agrave;y 6 cm, ho&agrave;n to&agrave;n kh&ocirc;ng sử dụng th&eacute;p hay b&ecirc; t&ocirc;ng.</li>\r\n	<li><strong>Tham quan Phố Cổ Kh&ecirc; Bố</strong>.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng địa phương, đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch tham quan Trung t&acirc;m nghi&ecirc;n cứu Trung Y, tại đ&acirc;y qu&yacute; kh&aacute;ch ng&acirc;m ch&acirc;n với thảo dược.</li>\r\n</ul>\r\n\r\n<p>Đo&agrave;n khởi h&agrave;nh đi Trương Gia Giới, ăn tối tại nh&agrave; h&agrave;ng.<br />\r\nXe đưa đo&agrave;n ra s&acirc;n bay, đ&aacute;p chuyến bay&nbsp;<strong><em>VJ2553 (21:30 &ndash; 01:45+1)</em></strong>&nbsp;&nbsp;về TP.HCM. Kết th&uacute;c chương tr&igrave;nh tham quan. Đo&agrave;n chia tay, hẹn gặp lại qu&yacute; kh&aacute;ch trong những h&agrave;nh tr&igrave;nh tiếp theo của Du Lịch Việt.<br />\r\nĐến nơi, đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng. Sau đ&oacute;, đo&agrave;n tham quan:</p>\r\n', '2021-11-24', ' 6 ngày 5 đêm', ' Từ Hồ Chí Minh  ', 12, 26900000, 1, 2, 0),
(12, 'Du lịch Nhật Bản mùa hoa Anh Đào 2020 từ Sài Gòn giá tốt', 'Du lịch Nhật Bản - Du lịch Nhật Bản mùa hoa Anh Đàoluôn là tâm điểm vào dịp cuối tháng 3, đầu tháng 4. Thời khắc sang xuân, tiết trời ấm áp, khắp nơi trên nước Nhật picnic, hội họp gia đình, bạn bè, đồng nghiệp ngắm hoa Anh Đào, thưởng rượu sake. Trời đất hòa hợp, người vui phơi phới, cảnh đẹp tuyệt vời, khó có thể nói hết lời. \r\n\r\ntag: Tour Nhật Bản mùa hoa Anh Đào Tour du lịch Nhật Bản mùa hoa Anh Đào Du lich Nhat Ban hoa Anh Dao Mùa hoa Anh Đào Nhật Bản .', '1637746674_du-lich-nhat-ban-mua-hoa-anh-dao-gia-re-2020-du-lich-viet.jpg', '1637746674_du-lich-nhat-ban-mua-hoa-anh-dao-gia-khuyen-mai-du-lich-viet.jpg', '1637746674_du-lich-phuong-hoang-co-tran-gia-re-du-lich-viet.jpg', '1637746674_du-lich-nhat-ban-hoa-anh-dao-gia-re-202-du-lich-viet.jpg', '<p>NG&Agrave;Y 1 |&nbsp;TP. HCM &ndash; HANEDA (Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; mặt tại s&acirc;n bay T&acirc;n Sơn Nhất, Hướng dẫn vi&ecirc;n hướng dẫn Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay&nbsp;&nbsp;<strong><em>JL070 (23:50 &ndash; 06:55)</em></strong>&nbsp;đi Haneda(Tokyo). Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m m&aacute;y bay.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;HANEDA &ndash; HAKONE - FUJI (Ăn hai bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n đến s&acirc;n bay quốc tế Haneda (Tokyo), l&agrave;m thủ tục nhập cảnh v&agrave; nhận h&agrave;nh l&yacute;. Xe đ&oacute;n đo&agrave;n khởi h&agrave;nh đi Fuji tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>N&uacute;i Ph&uacute; Sĩ</strong>&nbsp; - Một trong những biểu tượng nổi tiếng nhất Nhật Bản, Ph&uacute; Sĩ l&agrave; ngọn n&uacute;i cao nhất v&agrave; l&agrave; một trong &ldquo;Ba n&uacute;i Th&aacute;nh&rdquo; của quốc gia. Ngọn n&uacute;i lửa vẫn hoạt động cao 3.776m nổi tiếng với d&aacute;ng h&igrave;nh ch&oacute;p gần như ho&agrave;n hảo v&agrave; l&agrave; một ngọn n&uacute;i thi&ecirc;ng trong Thần Đạo Nhật Bản. Trong văn h&oacute;a Nhật, nhiều người tinh rằng n&uacute;i l&agrave; một vị &aacute; thần c&oacute; linh hồn.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng địa phương. Đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li>C&ocirc;ng vi&ecirc;n Heiwa &ndash; tại đ&acirc;y Q&uacute;y kh&aacute;ch được chi&ecirc;m ngưỡng những sắc hoa anh đ&agrave;o nở rộ.<strong><em>&nbsp;( Q&uacute;y kh&aacute;ch vui l&ograve;ng lưu &yacute; do phụ thuộc v&agrave;o điều kiện thời tiết n&ecirc;n c&aacute;c loại hoa c&oacute; thể nở sớm hơn hoặc chậm hơn, mong Q&uacute;y kh&aacute;ch th&ocirc;ng cảm v&igrave; đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng)</em></strong></li>\r\n	<li><strong>L&agrave;ng cổ Oshino Hakkai</strong>&nbsp;&ndash; nằm ẩn m&igrave;nh dưới ch&acirc;n n&uacute;i Ph&uacute; Sĩ, nơi n&agrave;y được quy hoạch th&agrave;nh một điểm tham quan, nhưng tất cả những g&igrave; truyền thống vẫn c&ograve;n nguy&ecirc;n vẹn. Những ng&ocirc;i nh&agrave; trong l&agrave;ng vẫn c&oacute; người ở, ng&ocirc;i nh&agrave; phủ đầy r&ecirc;u, bốn bức tường treo những n&ocirc;ng sản kh&ocirc; dự trữ.</li>\r\n	<li>Q&uacute;y kh&aacute;ch tham quan mua sắm tại khu&nbsp;<strong>Gotemba Premium Outlets</strong></li>\r\n</ul>\r\n\r\n<p>Đo&agrave;n tiếp tục về kh&aacute;ch sạn, nhận ph&ograve;ng nghỉ ngơi v&agrave;&nbsp; d&ugrave;ng&nbsp;<strong>buffet tối với m&oacute;n Cua Tuyết&nbsp;</strong>&nbsp;tại kh&aacute;ch sạn.<br />\r\nTại đ&acirc;y, Q&uacute;y kh&aacute;ch được&nbsp;<strong>trải nghiệm mặc đồ Kimono m&ugrave;a h&egrave; (Yukata) v&agrave; tắm suối kho&aacute;ng n&oacute;ng Onsen</strong>&nbsp;nổi tiếng theo kiểu Nhật. &nbsp;Nghỉ đ&ecirc;m tại khu vực n&uacute;i Ph&uacute; Sỹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;FUJI &ndash; ODAIBA &ndash; TOKYO (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn, l&agrave; thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>Odaiba</strong>&nbsp;tham quan v&agrave; chụp h&igrave;nh tại to&agrave;n cảnh vịnh Odaiba như c&ocirc;ng vi&ecirc;n ven biển Odaba, cầu Rainbow nối giữa vịnh Odaiba v&agrave; Tokyo,&nbsp; tượng Nữ Thần Tự Do &ndash; m&ocirc;̣t bản sao bi&ecirc;̉u tượng của New York được chính quy&ecirc;̀n Pháp mang đ&ecirc;́n trong dịp kỉ ni&ecirc;̣m năm Pháp ở Nhật, trung t&acirc;m triển l&atilde;m Tokyo Big Sight, AquaCity Odaiba, Đặc biệt, Q&uacute;y kh&aacute;ch c&oacute; thể chụp h&igrave;nh với Robot khổng lồ Gumdam &hellip;&nbsp;</p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng, đo&agrave;n tiếp tục tham quan :</p>\r\n\r\n<ul>\r\n	<li><strong>Ch&ugrave;a Asakusa Kannon -</strong>&nbsp;ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng bậc nhất Tokyo, nơi diễn ra c&aacute;c lễ hội lớn của quốc gia c&ugrave;ng với truyền thuyết ra đời ng&ocirc;i đền b&iacute; ẩn.</li>\r\n	<li><strong>Th&aacute;p truyền h&igrave;nh Tokyo Sky Tree&nbsp;</strong>&ndash; Từ ch&ugrave;a ngắm nh&igrave;n v&agrave; chụp h&igrave;nh được to&agrave;n cảnh của th&aacute;p.</li>\r\n	<li><strong>Tự do mua sắm tại Akihabara.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Tokyo.</p>\r\n\r\n<p>NG&Agrave;Y 4 |&nbsp;TOKYO &ndash; TP.HCM (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn, l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li>Chụp ảnh b&ecirc;n ngo&agrave;i&nbsp;<strong>Ngoại th&agrave;nh Ho&agrave;ng Cung</strong>&nbsp;&ndash; nơi sinh sống của Ho&agrave;ng gia Nhật với khu&ocirc;n vi&ecirc;n vườn c&acirc;y cổ thụ.</li>\r\n	<li>Q&uacute;y kh&aacute;ch dạo dọc bờ&nbsp;<strong>s&ocirc;ng Meguro</strong>&nbsp;- l&agrave; m&ocirc;̣t trong top những địa đi&ecirc;̉m đ&ecirc;̉ thưởng l&atilde;m hoa anh đ&agrave;o, đặc bi&ecirc;̣t l&agrave; Yozakura (ngắm hoa anh đ&agrave;o ban đ&ecirc;m). C&oacute; r&acirc;́t nhi&ecirc;̀u c&acirc;y hoa anh đ&agrave;o tr&ocirc;̀ng dọc theo bờ s&ocirc;ng Meguro, rợp sắc hoa h&ocirc;̀ng ph&acirc;́n xinh xắn ng&atilde; b&oacute;ng xu&ocirc;́ng bờ k&ecirc;nh x&aacute;m th&acirc;̣t h&agrave;i h&ograve;a.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng, đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li>Dạo phố mua sắm tại khu&nbsp;<strong>phố Ginza.</strong></li>\r\n	<li>Tự do mua sắm tại trung t&acirc;m thương mại&nbsp;<strong>Aeon Mall.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng. Đến giờ, Đo&agrave;n di chuyển ra s&acirc;n bay Haneda. Qu&yacute; kh&aacute;ch l&agrave;m thủ tục check-in v&agrave; xuất cảnh về lại Tp. Hồ Ch&iacute; Minh tr&ecirc;n chuyến bay&nbsp;<strong><em>JL079 (01:25 &ndash; 05:15).</em></strong>&nbsp;Kết th&uacute;c chuyến tham quan.</p>\r\n', '2021-11-24', '4 ngày 3 đêm', 'TP Hồ Chí Minh', 4, 28990000, 1, 2, 0),
(13, 'Du lịch Nhật Bản Tokyo - Hakone - Fuji - Odaiba từ Sài Gòn 2022', 'Làng cổ Oshino Hakkai – nằm ẩn mình dưới chân núi Phú Sĩ, nơi này được quy hoạch thành một điểm tham quan, nhưng tất cả những gì truyền thống vẫn còn nguyên vẹn. Những ngôi nhà trong làng vẫn có người ở, ngôi nhà phủ đầy rêu, bốn bức tường treo những nông sản khô dự trữ.\r\nNúi Phú Sĩ  - Một trong những biểu tượng nổi tiếng nhất Nhật Bản, Phú Sĩ là ngọn núi cao nhất và là một trong “Ba núi Thánh” của quốc gia. Ngọn núi lửa vẫn hoạt động cao 3.776m nổi tiếng với dáng hình chóp gần như hoàn hảo và là một ngọn núi thiêng trong Thần Đạo Nhật Bản. Trong văn hóa Nhật, nhiều người tinh rằng núi là một vị á thần có linh hồn.', 'nuocngoai4.jpg', '1637746812_di-du-lich-nhat-ban-mua-he-gia-tot-du-lich-viet.jpg', '1637746812_tua-du-lich-nhat-ban-gia-khuyen-mai-du-lich-viet(1).jpg', '1637746812_shopping-tour-nhat-ban-gia-tot-du-lich-viet.jpg', '<p>|&nbsp;Đ&Ecirc;M TRƯỚC NG&Agrave;Y KHỞI H&Agrave;NH: TP. HCM &ndash; NARITA (Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; mặt tại s&acirc;n bay T&acirc;n Sơn Nhất, Hướng dẫn vi&ecirc;n hướng dẫn Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay&nbsp;<strong><em>VJ822 (00:15 &ndash; 08:00</em></strong>) đi Narita(Tokyo). Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m m&aacute;y bay.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 1 |&nbsp;NARITA &ndash; HAKONE &ndash; FUJI (Ăn hai bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n đến s&acirc;n bay quốc tế Narita (Tokyo), l&agrave;m thủ tục nhập cảnh v&agrave; nhận h&agrave;nh l&yacute;. Xe đ&oacute;n đo&agrave;n khởi h&agrave;nh đi Hakone.<br />\r\nĂn trưa tại nh&agrave; h&agrave;ng địa phương, đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Khu vườn tr&aacute;i c&acirc;y sạch &ndash; t</strong>ại đ&acirc;y, Q&uacute;y kh&aacute;ch c&oacute; thể tự tay h&aacute;i v&agrave; thưởng thức những tr&aacute;i c&acirc;y tươi ngon như d&acirc;u, nho, tao, đ&agrave;o, cherry&hellip;&nbsp;<em>( T&ugrave;y v&agrave;o t&igrave;nh h&igrave;nh thực tế thời tiết v&agrave; theo m&ugrave;a)</em></li>\r\n	<li>Qu&yacute; kh&aacute;ch tiếp tục tự do tham quan mua sắm tại&nbsp;<strong>Gotemba Shopping Mall</strong></li>\r\n</ul>\r\n\r\n<p>Đo&agrave;n tiếp tục về kh&aacute;ch sạn, nhận ph&ograve;ng nghỉ ngơi v&agrave;&nbsp; d&ugrave;ng&nbsp;<strong>buffet tối với m&oacute;n Cua Tuyết&nbsp;</strong>&nbsp;tại kh&aacute;ch sạn.<br />\r\nTại đ&acirc;y, Q&uacute;y kh&aacute;ch được&nbsp;<strong>trải nghiệm mặc đồ Kimono m&ugrave;a h&egrave; (Yukata) v&agrave; tắm suối kho&aacute;ng n&oacute;ng Onsen</strong>&nbsp;nổi tiếng theo kiểu Nhật. &nbsp;Nghỉ đ&ecirc;m tại khu vực n&uacute;i Ph&uacute; Sỹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;FUJI &ndash; TOKYO (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn sang tại kh&aacute;ch sạn, l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>L&agrave;ng cổ Oshino Hakkai</strong>&nbsp;&ndash; nằm ẩn m&igrave;nh dưới ch&acirc;n n&uacute;i Ph&uacute; Sĩ, nơi n&agrave;y được quy hoạch th&agrave;nh một điểm tham quan, nhưng tất cả những g&igrave; truyền thống vẫn c&ograve;n nguy&ecirc;n vẹn. Những ng&ocirc;i nh&agrave; trong l&agrave;ng vẫn c&oacute; người ở, ng&ocirc;i nh&agrave; phủ đầy r&ecirc;u, bốn bức tường treo những n&ocirc;ng sản kh&ocirc; dự trữ.</li>\r\n	<li><strong>N&uacute;i Ph&uacute; Sĩ</strong>&nbsp; - Một trong những biểu tượng nổi tiếng nhất Nhật Bản, Ph&uacute; Sĩ l&agrave; ngọn n&uacute;i cao nhất v&agrave; l&agrave; một trong &ldquo;Ba n&uacute;i Th&aacute;nh&rdquo; của quốc gia. Ngọn n&uacute;i lửa vẫn hoạt động cao 3.776m nổi tiếng với d&aacute;ng h&igrave;nh ch&oacute;p gần như ho&agrave;n hảo v&agrave; l&agrave; một ngọn n&uacute;i thi&ecirc;ng trong Thần Đạo Nhật Bản. Trong văn h&oacute;a Nhật, nhiều người tinh rằng n&uacute;i l&agrave; một vị &aacute; thần c&oacute; linh hồn.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng, đo&agrave;n khởi h&agrave;nh về Tokyo tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Q&uacute;y kh&aacute;ch chi&ecirc;m ngưỡng lễ hội &aacute;nh sang m&ugrave;a đ&ocirc;ng tại c&ocirc;ng vi&ecirc;n Yumiuriland.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng địa phương, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Tokyo.</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;TOKYO - ODAIBA (Ăn ba bữa) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn sang tại kh&aacute;ch sạn, l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh đi Odaiba tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Vịnh Odaiba -</strong>&nbsp;Btham quan v&agrave; chụp h&igrave;nh tại to&agrave;n cảnh vịnh Odaiba như c&ocirc;ng vi&ecirc;n ven biển Odaba, cầu Rainbow nối giữa vịnh Odaiba v&agrave; Tokyo,&nbsp; tượng Nữ Thần Tự Do &ndash; m&ocirc;̣t bản sao bi&ecirc;̉u tượng của New York được chính quy&ecirc;̀n Pháp mang đ&ecirc;́n trong dịp kỉ ni&ecirc;̣m năm Pháp ở Nhật, trung t&acirc;m triển l&atilde;m Tokyo Big Sight, AquaCity Odaiba, Đặc biệt, Q&uacute;y kh&aacute;ch c&oacute; thể chụp h&igrave;nh với Robot khổng lồ Gumdam &hellip;</li>\r\n	<li>Chụp ảnh b&ecirc;n ngo&agrave;i&nbsp;<strong>Ngoại th&agrave;nh Ho&agrave;ng Cung</strong>&nbsp;&ndash; nơi sinh sống của Ho&agrave;ng gia Nhật với khu&ocirc;n vi&ecirc;n vườn c&acirc;y cổ thụ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn trưa tại nh&agrave; h&agrave;ng. Đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Ch&ugrave;a Asakusa Kannon -</strong>&nbsp;ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng bậc nhất Tokyo, nơi diễn ra c&aacute;c lễ hội lớn của quốc gia c&ugrave;ng với truyền thuyết ra đời ng&ocirc;i đền b&iacute; ẩn.</li>\r\n	<li><strong>Th&aacute;p truyền h&igrave;nh Tokyo Sky Tree&nbsp;</strong>&ndash; Từ ch&ugrave;a ngắm nh&igrave;n v&agrave; chụp h&igrave;nh được to&agrave;n cảnh của th&aacute;p.</li>\r\n	<li><strong>Mua sắm tại trung t&acirc;m thương mại Aeon Mall Narita.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Narita.</p>\r\n\r\n<p>NG&Agrave;Y 4 |&nbsp;NARITA &ndash; HỒ CH&Iacute; MINH (Ăn s&aacute;ng) (Xem th&ecirc;m)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m thủ tục trả ph&ograve;ng, Đo&agrave;n di chuyển ra s&acirc;n bay Narita, ăn s&aacute;ng fastfood tr&ecirc;n xe. Qu&yacute; kh&aacute;ch l&agrave;m thủ tục check-in v&agrave; xuất cảnh về lại Tp. Hồ Ch&iacute; Minh tr&ecirc;n chuyến bay&nbsp;<strong><em>VJ823 (08:55 &ndash; 13:00).</em></strong>&nbsp;Kết th&uacute;c chuyến tham quan.</p>\r\n', '2021-11-24', ' 4 ngày 4 đêm', ' Từ Hồ Chí Minh  ', 8, 23999000, 1, 2, 0),
(14, 'Nha Trang - Biển Nhũ Tiên - Vinwonders', 'Nha Trang với khí hậu ôn hòa, biển xanh trong quanh năm cùng những điểm vui chơi bậc nhất và không ngừng đổi mới hằng ngày luôn thu hút du khách gần xa. Đến với Nha Trang, du khách không chỉ tận hưởng những đợt gió biển trong nắng ấm mà còn có dịp thưởng thức hải sản tươi ngon cùng sự chào đón nồng hậu từ những người dân vùng biển nghĩa tình, cho Quý khách trải nghiệm khó quên tại vùng đất này.', '1637747300_tfd_211104032356_043371 (1).jpg', '1637747300_tfd_211104032316_686362.jpg', '1637747300_tfd_211104032356_043371.jpg', '1637747300_tfd_211104032024_760555.jpg', '<h2>Ng&agrave;y 1 - TP.HỒ CH&Iacute; MINH - NHA TRANG (Ăn trưa, chiều)</h2>\r\n\r\n<p>Qu&yacute; kh&aacute;ch tập trung tạị điểm hẹn, ga đi Trong Nước S&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank">Vietravel</a>&nbsp;hỗ trợ l&agrave;m thủ tục cho đo&agrave;n đ&aacute;p chuyến bay đi Nha Trang. Đến S&acirc;n bay Cam Ranh, xe v&agrave; hướng dẫn vi&ecirc;n Vietravel đ&oacute;n đo&agrave;n đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Th&aacute;p B&agrave; Ponagar:&nbsp;</strong>c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu cho nghệ thuật kiến tr&uacute;c v&agrave; đi&ecirc;u khắc t&iacute;n ngưỡng t&ocirc;n gi&aacute;o bậc nhất của d&acirc;n tộc Chăm tại Nha Trang.</li>\r\n	<li><strong>H&ograve;n Chồng:&nbsp;</strong>một thắng cảnh tự nhi&ecirc;n nằm ở bờ biển ph&iacute;a Bắc th&agrave;nh phố Nha Trang. Nơi đ&acirc;y du kh&aacute;ch c&oacute; thể di chuyển v&agrave;i bước đ&atilde; chạm đến s&oacute;ng biển hoặc ch&acirc;n đồi. Nhiều người bảo, H&ograve;n Chồng l&agrave; nơi giao nhau giữa biển v&agrave; n&uacute;i. Quần thể đ&aacute; H&ograve;n Chồng từ l&acirc;u đ&atilde; trở th&agrave;nh điểm&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank">du lịch</a>&nbsp;gi&agrave;u t&iacute;nh nh&acirc;n văn. Điều kỳ th&uacute; l&agrave; những tảng đ&aacute; lớn nằm chồng chất l&ecirc;n nhau bao đời nay nhưng s&oacute;ng biển v&agrave; mưa gi&oacute; kh&ocirc;ng thể x&ocirc; ng&atilde;.</li>\r\n</ul>\r\n\r\n<p>Buổi chiều, đo&agrave;n l&ecirc;n<strong>&nbsp;Emperor Nha Trang Cruise</strong>&nbsp;(chi ph&iacute; tự t&uacute;c), t&agrave;u sẽ di chuyển dọc th&agrave;nh phố biển Nha Trang, du kh&aacute;ch sẽ được ngắm cảnh ho&agrave;ng h&ocirc;n đẹp nhất trong khi thưởng thức tiệc cocktail, bữa tối tinh tế v&agrave; nghệ sỹ ghi ta, violon chơi những bản nhạc du dương trong một khung cảnh l&atilde;ng mạn dưới trời sao, &aacute;nh trăng v&agrave; &aacute;nh đ&egrave;n rực rỡ của th&agrave;nh phố.<br />\r\nXe đưa đo&agrave;n về lại th&agrave;nh phố Nha Trang, nghỉ đ&ecirc;m.</p>\r\n\r\n<h2>Ng&agrave;y 2 - NHA TRANG - VINWONDERS (Ăn s&aacute;ng, ăn trưa v&agrave; tối tự t&uacute;c)</h2>\r\n\r\n<p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n ra bến cảng đi c&aacute;p treo sang Vinpearl tham quan v&agrave; trải nghiệm&nbsp;<strong>Vinwonders Nha Trang&nbsp;</strong>đầy sắc m&agrave;u (bao gồm v&eacute; tham quan): ngắm to&agrave;n cảnh Vịnh Nha Trang từ đỉnh v&ograve;ng xoay mặt trời lớn nhất Việt Nam, tham quan bộ sưu tập &ldquo;kỳ hoa, dị thảo&rdquo; tại đồi Vạn Hoa, khu vườn th&uacute; mở - vườn Qu&yacute; Vương, tham gia c&aacute;c tr&ograve; chơi tương t&aacute;c v&agrave; li&ecirc;n ho&agrave;n tại Quảng trường Thần Thoại hay V&ugrave;ng đất y&ecirc;u thương, xem phim 4D tại l&acirc;u đ&agrave;i Đại Dương, kh&aacute;m ph&aacute; khu tr&ograve; chơi cảm gi&aacute;c mạnh, khu tr&ograve; chơi trong nh&agrave; hay tắm biển, tắm hồ bơi lớn nhất Đ&ocirc;ng Nam &Aacute;&hellip; v&agrave; thưởng thức chương tr&igrave;nh biểu diễn Nhạc Nước hiện đại. Đo&agrave;n tự do d&ugrave;ng bữa tối tại đ&acirc;y v&agrave; tiếp tục tham gia c&aacute;c hoạt động buổi tối tại Vinpearl đến giờ về lại đất liền. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n\r\n<p><em><strong>**Tham quan Vinwonder v&agrave;o th&aacute;ng 11 sẽ được tặng voucher ăn trưa tại khu vui chơi.</strong></em><br />\r\n&nbsp;</p>\r\n\r\n<h2>Ng&agrave;y 3 - NHA TRANG - BIỂN NHŨ TI&Ecirc;N - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng)</h2>\r\n\r\n<p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Xe đưa đo&agrave;n tham quan v&agrave; thư gi&atilde;n tại:&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong><a href="https://travel.com.vn/nha-trang/tour-bien-nhu-tien.aspx?utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank">Biển Nhũ Ti&ecirc;n</a>:</strong>&nbsp;đắm m&igrave;nh trong l&agrave;n nước biển trong xanh, m&aacute;t mẻ, được bao bọc bởi b&atilde;i c&aacute;t d&agrave;i trắng x&oacute;a v&agrave; những h&agrave;ng dừa lao xao trong gi&oacute;. Với vẻ đẹp hoang sơ, thơ mộng, biển Nhũ Ti&ecirc;n tựa như n&agrave;ng ti&ecirc;n đang ngủ v&ugrave;i giữa kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng của s&oacute;ng, của gi&oacute;, của n&uacute;i v&agrave; của m&acirc;y. Xe đưa đo&agrave;n ra s&acirc;n bay Cam Ranh đ&oacute;n chuyến bay về Tp. Hồ Ch&iacute; Minh. Đến nơi, chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank">du lịch</a>&nbsp;tại s&acirc;n bay T&acirc;n Sơn Nhất.</li>\r\n</ul>\r\n\r\n<p><em><strong>** Chuyến bay sau 14h sẽ th&ecirc;m bữa ăn trưa</strong></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ghi ch&uacute;:</strong></p>\r\n\r\n<ul>\r\n	<li>H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;</li>\r\n	<li>Lịch tr&igrave;nh tham quan rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.</li>\r\n	<li>Đ&acirc;y l&agrave; chương tr&igrave;nh du lịch trọn g&oacute;i,&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank">Vietravel</a>&nbsp;kh&ocirc;ng c&oacute; tr&aacute;ch nhiệm ho&agrave;n trả chi ph&iacute; ch&ecirc;nh lệch cho c&aacute;c kh&aacute;ch h&agrave;ng thuộc diện miễn giảm hoặc đối tượng ưu ti&ecirc;n như trẻ nhỏ, người lớn tuổi, người c&oacute; c&ocirc;ng với c&aacute;ch mạng&hellip; Ch&iacute;nh s&aacute;ch miễn giảm, ưu ti&ecirc;n (nếu c&oacute;) chỉ d&agrave;nh cho kh&aacute;ch lẻ của c&aacute;c điểm tham quan.</li>\r\n</ul>\r\n\r\n<p><strong>Điều kiện nhận kh&aacute;ch:&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Đảm bảo nguy&ecirc;n tắc 5K.</li>\r\n	<li>Vận chuyển kh&ocirc;ng qu&aacute; 50% số ghế, kh&ocirc;ng qu&aacute; 20 kh&aacute;ch/1 xe (bao gồm cả t&agrave;i xế), tu&acirc;n thủ ngồi gi&atilde;n c&aacute;ch tr&ecirc;n xe v&agrave; đeo khẩu trang suốt qu&aacute; tr&igrave;nh di chuyển.</li>\r\n	<li>Qu&yacute; kh&aacute;ch đ&atilde; ho&agrave;n th&agrave;nh ti&ecirc;m 2 mũi vaccine ngừa Covid-19 v&agrave; c&oacute; thẻ xanh hoặc giấy x&aacute;c nhận ti&ecirc;m chủng (mũi thứ 2 đủ 14 ng&agrave;y so với ng&agrave;y khởi h&agrave;nh) hoặc Qu&yacute; kh&aacute;ch c&oacute; giấy x&aacute;c nhận đ&atilde; khỏi bệnh trong v&ograve;ng 6 th&aacute;ng.&nbsp;</li>\r\n	<li>Qu&yacute; kh&aacute;ch cần c&oacute; giấy chứng nhận kết quả PCR/ test nhanh &acirc;m t&iacute;nh hiệu lực trong v&ograve;ng 72H &aacute;p dụng cho cả người lớn v&agrave; trẻ em. &nbsp;</li>\r\n</ul>\r\n', '2021-11-24', '3 ngày', 'tp Hồ Chí Minh', 10, 13000000, 1, 1, 0);
INSERT INTO `tour` (`id_tour`, `ten`, `tomTat`, `hinh1`, `hinh2`, `hinh3`, `hinh4`, `lichTrinh`, `ngay`, `thoiGian`, `xuatPhat`, `soLuongNguoi`, `gia`, `trangThai`, `id_theLoai`, `id_taiKhoan`) VALUES
(15, 'Tham quan Safari World & Thỏa Thích Vui Chơi Tại Vinwonder - Trải nghiệm dịch vụ Nghỉ dưỡng VinHolid', 'Khám phá một số hạng mục nổi bật tại khu Grand World như: công trình tre, công viên nghệ thuật đương đại thuộc Open Park…; tản bộ bên dòng “kênh đào Venice” và nhìn ngắm những chiếc thuyền Gondola, khu phố shophouse lộng lẫy sắc màu, cổng lâu đài tráng lệ, ba cây cầu vòm bán nguyệt...;', '1637747661_tfd_211112025447_946530.jpg', '1637747661_tfd_211112025021_759448.jpg', '1637747661_dc_211112_GRAND WORLD (4).jpg', '1637747661_tfd_211112025606_018157.jpg', '<h2>Ng&agrave;y 1 - TP. HỒ CH&Iacute; MINH - PH&Uacute; QUỐC - GRAND WORLD (Ăn chiều)</h2>\r\n\r\n<p>Qu&yacute; kh&aacute;ch tập trung tại S&acirc;n bay T&acirc;n Sơn Nhất, ga đi Trong Nước. Hướng dẫn vi&ecirc;n l&agrave;m thủ tục cho Qu&yacute; kh&aacute;ch chuyến bay chiều ph&uacute;t đi Ph&uacute; Quốc. Xe đ&oacute;n đo&agrave;n tại s&acirc;n bay đưa về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi tại&nbsp;<strong>Vinholidays&nbsp;</strong>- Chuỗi kh&aacute;ch sạn th&ocirc;ng minh được thiết kế hiện đại, dịch vụ linh hoạt c&ugrave;ng c&aacute;c tiện nghi quốc tế. Buổi tối, Qu&yacute; kh&aacute;ch c&oacute; thể tự do kh&aacute;m ph&aacute; một số hạng mục tại khu&nbsp;<strong>Grand World</strong>&nbsp;như:&nbsp;<strong>c&ocirc;ng tr&igrave;nh tre, c&ocirc;ng vi&ecirc;n nghệ thuật đương đại thuộc Open Park</strong>&hellip;; tản bộ b&ecirc;n d&ograve;ng&nbsp;<strong>&ldquo;k&ecirc;nh đ&agrave;o Venice&rdquo;&nbsp;</strong>v&agrave; nh&igrave;n ngắm những chiếc thuyền Gondola, khu phố shophouse lộng lẫy sắc m&agrave;u, cổng l&acirc;u đ&agrave;i tr&aacute;ng lệ, ba c&acirc;y cầu v&ograve;m b&aacute;n nguyệt...; Thưởng thức bữa tiệc &aacute;nh s&aacute;ng đỉnh cao với show<strong>&nbsp;&ldquo;Sắc m&agrave;u Venice&rdquo;</strong></p>\r\n\r\n<p><strong>(show diễn dự kiến diễn ra từ ng&agrave;y 12/11/2021, c&oacute; thể thay đổi t&ugrave;y theo t&igrave;nh h&igrave;nh thực tế)</strong></p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Vinholidays.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ng&agrave;y 2 - VƯỜN TH&Uacute; B&Aacute;N HOANG D&Atilde; VINPEARL SAFARI - VUI CHƠI THỎA TH&Iacute;CH TẠI C&Ocirc;NG VI&Ecirc;N CHỦ ĐỀ VINWONDERS PH&Uacute; QUỐC (Ăn s&aacute;ng, trưa, chiều)</h2>\r\n\r\n<p>Sau khi d&ugrave;ng bữa s&aacute;ng tại<strong>&nbsp;VinHolidays</strong>, Qu&yacute; kh&aacute;ch sẽ đến kh&aacute;m ph&aacute;&nbsp;<strong>Vinpearl Safari Ph&uacute; Quốc</strong>&nbsp;- Vườn th&uacute; hoang d&atilde; đầu ti&ecirc;n tại Việt Nam: với quy m&ocirc; 180 ha, hơn 130 lo&agrave;i động vật qu&yacute; hiếm, du kh&aacute;ch thưởng thức c&aacute;c chương tr&igrave;nh biểu diễn, chụp ảnh với động vật; trải nghiệm vườn th&uacute; mở trong rừng tự nhi&ecirc;n, gần gũi v&agrave; th&acirc;n thiện với con người.<br />\r\nBuổi chiều, Qu&yacute; kh&aacute;ch tham quan<strong>&nbsp;Khu vui chơi giải tr&iacute; VinWonders</strong>&nbsp;- du kh&aacute;ch sẽ lạc v&agrave;o &ldquo;Thế Giới Cổ T&iacute;ch&rdquo; th&ocirc;ng qua những tr&ograve; chơi tương t&aacute;c lần đầu ti&ecirc;n xuất hiện tại Việt Nam với 6 chủ đề kh&aacute;c nhau:<em><strong>&nbsp;</strong>Khu Cảm Gi&aacute;c Mạnh - Thế Giới Phi&ecirc;u Lưu; Khu C&ocirc;ng Vi&ecirc;n Nước - Thế Giới Lốc Xo&aacute;y; Khu L&acirc;u Đ&agrave;i Trung T&acirc;m - Ch&acirc;u &Acirc;u Trung Cổ; Khu Cổ T&iacute;ch - Thế Giới Diệu Kỳ; Khu Viking - Ng&ocirc;i L&agrave;ng B&iacute; Mật.</em><br />\r\nNghỉ đ&ecirc;m tại VinHolidays.<br />\r\n&nbsp;</p>\r\n\r\n<h2>Ng&agrave;y 3 - PH&Uacute; QUỐC - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</h2>\r\n\r\n<p>D&ugrave;ng buffet s&aacute;ng tại VinHolidays. Đo&agrave;n trả ph&ograve;ng kh&aacute;ch sạn.&nbsp;&nbsp;Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi tham quan:<br />\r\n-&nbsp;<strong>Dinh Cậu</strong>: biểu tượng văn ho&aacute; v&agrave; t&iacute;n ngưỡng của đảo Ph&uacute; Quốc. Nơi ngư d&acirc;n địa phương gởi gắm niềm tin cho một chuyến ra khơi đ&aacute;nh bắt đầy ắp c&aacute; khi trở về. Sau đ&oacute;, đo&agrave;n viếng&nbsp;<strong>Dinh B&agrave; Thủy Long Th&aacute;nh Mẫu</strong>&nbsp;- Thờ Thần Nữ Kim Giao, người phụ nữ được người d&acirc;n Ph&uacute; Quốc rất mực t&ocirc;n k&iacute;nh v&igrave; c&oacute; c&ocirc;ng khai ph&aacute; huyện đảo n&agrave;y.<br />\r\n- Tham quan v&agrave; mua sắm đặc sản nổi tiếng tại&nbsp;<strong>Vườn Ti&ecirc;u; Nh&agrave; th&ugrave;ng nước mắm; Đặc sản kh&ocirc;; Cơ sở sản xuất ngọc trai Ph&uacute; Quốc</strong>&nbsp;- trưng b&agrave;y c&aacute;c trang sức bằng ngọc trai ch&iacute;nh hiệu được nu&ocirc;i cấy tại Ph&uacute; Quốc.&nbsp;<br />\r\nSau khi tham quan, xe đưa Qu&yacute; kh&aacute;ch đi ăn trưa v&agrave; ra s&acirc;n bay Ph&uacute; Quốc đ&aacute;p chuyến bay trở về TP. Hồ Ch&iacute; Minh. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&amp;utm_medium=click&amp;utm_campaign=ATLinks" target="_blank">du lịch</a>&nbsp;tại s&acirc;n bay T&acirc;n Sơn Nhất.</p>\r\n\r\n<p>*********************</p>\r\n\r\n<p><strong>Điều kiện nhận kh&aacute;ch:</strong>&nbsp;Nhận kh&aacute;ch địa phương/ ngoại tỉnh từ địa phương nguy cơ thấp v&agrave; trung b&igrave;nh (v&ugrave;ng xanh v&agrave; v&agrave;ng)<br />\r\n-&nbsp;&nbsp;&nbsp;&nbsp;Đối với kh&aacute;ch từ Bờ ra đảo, hoặc đến bằng đường h&agrave;ng kh&ocirc;ng:<br />\r\n+&nbsp;&nbsp;&nbsp;Qu&yacute; kh&aacute;ch đ&atilde; ho&agrave;n th&agrave;nh ti&ecirc;m 2 mũi vaccine ngừa Covid-19 v&agrave; c&oacute; thẻ xanh hoặc giấy x&aacute;c nhận ti&ecirc;m chủng (mũi thứ 2 đủ 14 ng&agrave;y so với ng&agrave;y khởi h&agrave;nh) hoặc những người c&oacute; giấy x&aacute;c nhận đ&atilde; khỏi bệnh trong v&ograve;ng 6 th&aacute;ng.</p>\r\n\r\n<p>+ C&oacute; kết quả test nhanh hoặc RT-PCR&nbsp;&acirc;m t&iacute;nh trong v&ograve;ng 72h trước khi đến Ki&ecirc;n Giang.<br />\r\n+ Kh&aacute;ch nước ngo&agrave;i lưu tr&uacute; tại Việt Nam thực hiện theo quy định như kh&aacute;ch nội địa.<br />\r\n+ Trẻ em, học sinh đi c&ugrave;ng người th&acirc;n chưa ti&ecirc;m vaccine th&igrave; cần kết quả x&eacute;t nghiệm PCR&nbsp;&nbsp;&acirc;m t&iacute;nh trong v&ograve;ng 72h kể từ khi nhận kết quả.&nbsp;</p>\r\n\r\n<p>+ K&yacute; cam kết v&agrave; chấp h&agrave;nh quy định ph&ograve;ng chống dịch<br />\r\n+ Khai b&aacute;o y tế hoặc qu&eacute;t m&atilde; QR</p>\r\n\r\n<p>+&nbsp;Đảm bảo nguy&ecirc;n tắc 5K.&nbsp;Vận chuyển kh&ocirc;ng qu&aacute; 50% số ghế, kh&ocirc;ng qu&aacute; 20 kh&aacute;ch/1 xe (bao gồm cả t&agrave;i xế), tu&acirc;n thủ ngồi gi&atilde;n c&aacute;ch tr&ecirc;n xe v&agrave; đeo khẩu trang suốt qu&aacute; tr&igrave;nh di chuyển.</p>\r\n', '2021-11-24', '3 ngày', 'TP. Hồ Chí Minh', 6, 7390000, 1, 1, 0);

